#!/bin/bash
EMERALD='\x1b[38;5;85m';RESET='\033[0m';wrap_text(){ echo "$1"|fold -s -w 78; };draw_box(){ local content=("$@");local width=80;printf "${EMERALD}+";printf -- '-%.0s' $(seq 1 $width);printf "+${RESET}\n";for line in "${content[@]}";do wrap_text "$line"|while IFS= read -r l;do printf "${EMERALD}|${RESET} %-78s ${EMERALD}|${RESET}\n" "$l";done;done;printf "${EMERALD}+";printf -- '-%.0s' $(seq 1 $width);printf "+${RESET}\n";};display_card(){ clear;draw_box "Ace of Hearts: (H: Hydrogen, He: Helium, Li: Lithium)" "These elements are crucial in space exploration and technology." "Hydrogen is a primary fuel source, helium is essential for cooling systems," "and lithium is integral to battery technology.";echo;draw_box "1. Hydrogen (H)" "2. Helium (He)" "3. Lithium (Li)" "" "Input required: Select an element (1-3) or press 0 to exit.";};display_element(){ local s="$1" nm="$2" z="$3" w="$4" d="$5";clear;draw_box "Element Details" "Symbol: $s" "Name: $nm" "Atomic Number: $z" "Atomic Weight: $w";echo;draw_box "Description:" "$d";};elements=("H|Hydrogen|1|1.008|Hydrogen is the lightest and most abundant element in the universe. It is used as a primary fuel for rockets and in energy generation systems. Hydrogen fuel cells are critical for powering spacecraft and rovers." "He|Helium|2|4.0026|Helium is a noble gas that is vital for cooling superconducting magnets in spacecraft and telescopes. It is also used in pressurizing rocket fuel tanks and as a breathing gas in space suits." "Li|Lithium|3|6.94|Lithium is a key component in rechargeable batteries used in spacecraft and satellites. Its lightweight nature makes it ideal for energy storage in space exploration.");while true;do display_card;echo -e "${EMERALD}";read -p "Enter your choice: " c;echo -e "${RESET}";if [[ "$c" == "0" ]];then clear;draw_box "Thank you!" "Exiting the Ace of Hearts educational card.";break;elif (( c>=1 && c<=3 ));then IFS='|' read -r s nm z w d <<<"${elements[$((c-1))]}";display_element "$s" "$nm" "$z" "$w" "$d";echo -e "${EMERALD}";read -n 1 -s -r -p "Press any key to return to the main menu...";echo -e "${RESET}";else draw_box "Invalid Input" "Please enter a valid choice (1-3) or 0 to exit.";sleep 2;fi;done
