#!/bin/bash
E='\x1b[38;5;85m';R='\033[0m';wrap(){ echo "$1"|fold -s -w 78; };box(){ local l=("$@");w=80;printf "${E}+%s+${R}\n" "$(printf -- '-%.0s' $(seq 1 $w))";for i in "${l[@]}";do wrap "$i"|while IFS= read -r x;do printf "${E}|${R} %-78s ${E}|${R}\n" "$x";done;done;printf "${E}+%s+${R}\n" "$(printf -- '-%.0s' $(seq 1 $w))";};card(){ clear;box "4 of Hearts: (Ne: Neon, Na: Sodium, Mg: Magnesium)" "These elements are significant for lighting, biological processes, and structural materials." "Neon is used in visual displays, sodium in life-support systems, and magnesium" "in strong, lightweight alloys for spacecraft.";echo;box "1. Neon (Ne)" "2. Sodium (Na)" "3. Magnesium (Mg)" "" "Input required: Select an element (1-3) or press 0 to exit.";};detail(){ s="$1";n="$2";z="$3";w="$4";d="$5";clear;box "Element Details" "Symbol: $s" "Name: $n" "Atomic Number: $z" "Atomic Weight: $w";echo;box "Description:" "$d";};e=("Ne|Neon|10|20.18|Neon is a noble gas primarily used in lighting, such as indicators in spacecraft control panels. Its inert properties and bright glow make it highly effective for visibility in low-light environments." "Na|Sodium|11|22.99|Sodium is critical for maintaining water balance in life-support systems. It also plays a role in heat exchangers for cooling in spacecraft and reactors." "Mg|Magnesium|12|24.31|Magnesium is a lightweight metal used in aerospace alloys. It is essential for creating strong, durable, and lightweight components needed for efficient spacecraft design.");while :;do card;echo -e "${E}";read -p "Enter your choice: " c;echo -e "${R}";[[ "$c" == "0" ]]&&clear&&box "Thank you!" "Exiting the 4 of Hearts educational card."&&break;(( c>=1 && c<=3 ))&&IFS='|' read -r s n z w d<<<"${e[$((c-1))]}"&&detail "$s" "$n" "$z" "$w" "$d"&&echo -e "${E}"&&read -n 1 -s -r -p "Press any key to return to the main menu..."&&echo -e "${R}"||box "Invalid Input" "Please enter a valid choice (1-3) or 0 to exit.";done
