#!/bin/bash
E='\x1b[38;5;85m';R='\033[0m';wrap(){ echo "$1"|fold -s -w 78; };box(){ local a=("$@");w=80;printf "${E}+";printf -- '-%.0s' $(seq 1 $w);printf "+${R}\n";for l in "${a[@]}";do wrap "$l"|while IFS= read -r x;do printf "${E}|${R} %-78s ${E}|${R}\n" "$x";done;done;printf "${E}+";printf -- '-%.0s' $(seq 1 $w);printf "+${R}\n";};card(){ clear;box "5 of Hearts: (Al: Aluminium, Si: Silicon, P: Phosphorus)" "These elements are essential for spacecraft structure, electronics, and biological systems." "Aluminium is used in lightweight materials, silicon in semiconductors, and phosphorus in life-support systems.";echo;box "1. Aluminium (Al)" "2. Silicon (Si)" "3. Phosphorus (P)" "" "Input required: Select an element (1-3) or press 0 to exit.";};element(){ clear;box "Element Details" "Symbol: $1" "Name: $2" "Atomic Number: $3" "Atomic Weight: $4";echo;box "Description:" "$5";};e=("Al|Aluminium|13|26.98|Aluminium is a lightweight metal extensively used in spacecraft construction. Its low density and high strength-to-weight ratio make it ideal for creating structural components and shielding." "Si|Silicon|14|28.09|Silicon is a semiconductor critical for electronic systems in spacecraft. It forms the basis of solar cells, microchips, and other devices essential for space missions." "P|Phosphorus|15|30.97|Phosphorus is a key element in life-support systems, playing a role in biological processes and energy storage. It is used in the form of phosphates for maintaining astronaut health.");while true;do card;echo -e "${E}";read -p "Enter your choice: " c;echo -e "${R}";if [[ "$c" == "0" ]];then clear;box "Thank you!" "Exiting the 5 of Hearts educational card.";break;elif (( c>=1 && c<=3 ));then IFS='|' read -r s n z w d <<<"${e[$((c-1))]}";element "$s" "$n" "$z" "$w" "$d";echo -e "${E}";read -n 1 -s -r -p "Press any key to return to the main menu...";echo -e "${R}";else box "Invalid Input" "Please enter a valid choice (1-3) or 0 to exit.";sleep 2;fi;done
