#!/bin/bash
E='\e[38;5;85m';R='\033[0m';w(){ echo "$1"|fold -s -w 78;};b(){ local c=("$@");W=80;printf "${E}+";printf -- '-%.0s' $(seq 1 $W);printf "+${R}\n";for l in "${c[@]}";do w "$l"|while IFS= read -r L;do printf "${E}|${R} %-78s ${E}|${R}\n" "$L";done;done;printf "${E}+";printf -- '-%.0s' $(seq 1 $W);printf "+${R}\n";};d(){ clear;b "8 of Hearts: (Ti: Titanium, V: Vanadium, Cr: Chromium)" "These elements are integral to creating strong, corrosion-resistant materials for spacecraft." "Titanium is lightweight and durable, vanadium strengthens alloys, and chromium prevents corrosion.";echo;b "1. Titanium (Ti)" "2. Vanadium (V)" "3. Chromium (Cr)" "" "Input required: Select an element (1-3) or press 0 to exit.";};e(){ clear;b "Element Details" "Symbol: $1" "Name: $2" "Atomic Number: $3" "Atomic Weight: $4";echo;b "Description:" "$5";};el=("Ti|Titanium|22|47.87|Titanium is widely used in aerospace applications due to its high strength, light weight, and resistance to corrosion. It is used in spacecraft frames, heat shields, and engines." "V|Vanadium|23|50.94|Vanadium is added to alloys to improve strength and toughness. It is crucial in creating materials that can withstand the extreme conditions of space travel." "Cr|Chromium|24|51.99|Chromium is used to prevent corrosion and oxidation in spacecraft components. Its addition to alloys enhances their durability in the harsh space environment.");while :;do d;echo -e "${E}";read -p "Enter your choice: " c;echo -e "${R}";[[ "$c" == "0" ]]&&clear&&b "Thank you!" "Exiting the 8 of Hearts educational card."&&break;(( c>=1&&c<=3 ))&&IFS='|' read -r s n z w d <<<"${el[$((c-1))]}"&&e "$s" "$n" "$z" "$w" "$d"&&echo -e "${E}"&&read -n 1 -s -r -p "Press any key to return to the main menu..."&&echo -e "${R}"||{ b "Invalid Input" "Please enter a valid choice (1-3) or 0 to exit.";};done
