#!/bin/bash
E='\x1b[38;5;85m';R='\033[0m';w(){ fold -s -w 78 <<<"$1"; };b(){ a=("$@"); printf "${E}+"; printf -- '-%.0s' $(seq 1 80); printf "+${R}\n"; for l in "${a[@]}"; do w "$l"|while IFS= read -r x;do printf "${E}|${R} %-78s ${E}|${R}\n" "$x";done;done; printf "${E}+"; printf -- '-%.0s' $(seq 1 80); printf "+${R}\n"; };d(){ clear;b "10 of Hearts: (Ni: Nickel, Cu: Copper, Zn: Zinc)" "These elements are fundamental for wiring, batteries, and corrosion resistance in spacecraft." "Nickel is used in batteries, copper in electrical systems, and zinc in protective coatings.";echo;b "1. Nickel (Ni)" "2. Copper (Cu)" "3. Zinc (Zn)" "" "Input required: Select an element (1-3) or press 0 to exit."; };s(){ clear;b "Element Details" "Symbol: $1" "Name: $2" "Atomic Number: $3" "Atomic Weight: $4";echo;b "Description:" "$5"; };e=("Ni|Nickel|28|58.69|Nickel is a key component in high-performance batteries used in spacecraft. Its durability and resistance to extreme conditions make it an ideal material for long-term energy storage." "Cu|Copper|29|63.55|Copper is essential for electrical systems in spacecraft. It is highly conductive and used extensively in wiring and communication systems for reliable performance." "Zn|Zinc|30|65.38|Zinc is used in protective coatings to prevent corrosion on spacecraft components. It ensures the longevity and reliability of critical structures exposed to harsh space environments."); while :;do d;echo -e "${E}";read -p "Enter your choice: " c;echo -e "${R}";[[ "$c" == 0 ]]&&clear&&b "Thank you!" "Exiting the 10 of Hearts educational card."&&break;(( c>=1 && c<=3 ))&&IFS='|' read -r y n a w m<<<"${e[$((c-1))]}"&&s "$y" "$n" "$a" "$w" "$m"&&echo -e "${E}"&&read -n1 -s -r -p "Press any key to return to the main menu..."&&echo -e "${R}"||b "Invalid Input" "Please enter a valid choice (1-3) or 0 to exit.";done
