#!/bin/bash
E='\x1b[38;5;85m';R='\033[0m';w(){ fold -s -w 78 <<<"$1"; };b(){ a=("$@"); printf "${E}+"; printf -- '-%.0s' $(seq 1 80); printf "+${R}\n"; for l in "${a[@]}"; do w "$l" | while IFS= read -r x; do printf "${E}|${R} %-78s ${E}|${R}\n" "$x"; done; done; printf "${E}+"; printf -- '-%.0s' $(seq 1 80); printf "+${R}\n"; };d(){ clear; b "Jack of Hearts: (Ga: Gallium, Ge: Germanium, As: Arsenic)" "These elements are critical in electronics, semiconductors, and specialized materials." "Gallium is used in solar panels, germanium in infrared optics, and arsenic in semiconductors."; echo; b "1. Gallium (Ga)" "2. Germanium (Ge)" "3. Arsenic (As)" "" "Input required: Select an element (1-3) or press 0 to exit."; };s(){ clear; b "Element Details" "Symbol: $1" "Name: $2" "Atomic Number: $3" "Atomic Weight: $4"; echo; b "Description:" "$5"; };e=("Ga|Gallium|31|69.72|Gallium is used in solar panels and LEDs for efficient energy conversion. It remains stable over a wide temperature range, making it ideal for space-based power systems." "Ge|Germanium|32|72.63|Germanium is a critical component in infrared optics and semiconductors. It is used in imaging systems and photovoltaic cells for space exploration." "As|Arsenic|33|74.92|Arsenic is used in semiconductor materials for high-frequency electronics and radiation-resistant devices. It ensures reliable communication and instrumentation in space missions."); while :; do d; echo -e "${E}"; read -p "Enter your choice: " c; echo -e "${R}"; [[ "$c" == 0 ]] && clear && b "Thank you!" "Exiting the Jack of Hearts educational card." && break; (( c>=1 && c<=3 )) && IFS='|' read -r y n a w m <<<"${e[$((c-1))]}" && s "$y" "$n" "$a" "$w" "$m" && echo -e "${E}" && read -n1 -s -r -p "Press any key to return to the main menu..." && echo -e "${R}" || b "Invalid Input" "Please enter a valid choice (1-3) or 0 to exit."; done
