#!/bin/bash
E='\x1b[38;5;85m';R='\033[0m';w(){ fold -s -w 78 <<<"$1"; };b(){ a=("$@");printf "${E}+";printf -- '-%.0s' $(seq 1 80);printf "+${R}\n";for l in "${a[@]}";do w "$l"|while IFS= read -r x;do printf "${E}|${R} %-78s ${E}|${R}\n" "$x";done;done;printf "${E}+";printf -- '-%.0s' $(seq 1 80);printf "+${R}\n";};d(){ clear;b "4 of Diamonds: (In: Indium, Sn: Tin, Sb: Antimony)" "These elements are vital for soldering, semiconductors, and specialized alloys." "Indium is used in touchscreens, tin in solder, and antimony in flame-retardant materials.";echo;b "1. Indium (In)" "2. Tin (Sn)" "3. Antimony (Sb)" "" "Input required: Select an element (1-3) or press 0 to exit.";};s(){ clear;b "Element Details" "Symbol: $1" "Name: $2" "Atomic Number: $3" "Atomic Weight: $4";echo;b "Description:" "$5";};e=("In|Indium|49|114.82|Indium is widely used in touchscreens and LCDs due to its excellent conductive properties. It is essential in space instrumentation and display systems." "Sn|Tin|50|118.71|Tin is a critical component in soldering materials used to connect electrical components in spacecraft. It ensures strong, reliable connections that withstand vibrations." "Sb|Antimony|51|121.76|Antimony is used in flame-retardant materials and specialized alloys for spacecraft components. Its inclusion enhances the safety and durability of materials under extreme conditions.");while :;do d;echo -e "${E}";read -p "Enter your choice: " c;echo -e "${R}";[[ "$c" == 0 ]]&&clear&&b "Thank you!" "Exiting the 4 of Diamonds educational card."&&break;(( c>=1 && c<=3 ))&&IFS='|' read -r y n a w m<<<"${e[$((c-1))]}"&&s "$y" "$n" "$a" "$w" "$m"&&echo -e "${E}"&&read -n1 -s -r -p "Press any key to return to the main menu..."&&echo -e "${R}"||b "Invalid Input" "Please enter a valid choice (1-3) or 0 to exit.";done
