#!/bin/bash
E='\x1b[38;5;85m';R='\033[0m';w(){ fold -s -w 78 <<<"$1"; };b(){ a=("$@");printf "${E}+";printf -- '-%.0s' $(seq 1 80);printf "+${R}\n";for l in "${a[@]}";do w "$l"|while IFS= read -r x;do printf "${E}|${R} %-78s ${E}|${R}\n" "$x";done;done;printf "${E}+";printf -- '-%.0s' $(seq 1 80);printf "+${R}\n";};d(){ clear;b "6 of Diamonds: (Cs: Cesium, Ba: Barium, La: Lanthanum)" "These elements are essential for atomic clocks, imaging technologies, and advanced optics." "Cesium is used in timekeeping, barium in radiology, and lanthanum in camera lenses and catalysts.";echo;b "1. Cesium (Cs)" "2. Barium (Ba)" "3. Lanthanum (La)" "" "Input required: Select an element (1-3) or press 0 to exit.";};s(){ clear;b "Element Details" "Symbol: $1" "Name: $2" "Atomic Number: $3" "Atomic Weight: $4";echo;b "Description:" "$5";};e=("Cs|Cesium|55|132.91|Cesium is used in atomic clocks for ultra-precise timekeeping, critical for navigation and synchronization in satellite and space systems." "Ba|Barium|56|137.33|Barium is used in medical imaging technologies, particularly in radiological applications. Its compounds provide detailed imaging for diagnostics in space medicine." "La|Lanthanum|57|138.91|Lanthanum is used in advanced camera lenses and as a catalyst in chemical processes. Its properties enhance imaging and energy production technologies for space exploration.");while :;do d;echo -e "${E}";read -p "Enter your choice: " c;echo -e "${R}";[[ "$c" == 0 ]]&&clear&&b "Thank you!" "Exiting the 6 of Diamonds educational card."&&break;(( c>=1 && c<=3 ))&&IFS='|' read -r y n a w m<<<"${e[$((c-1))]}"&&s "$y" "$n" "$a" "$w" "$m"&&echo -e "${E}"&&read -n1 -s -r -p "Press any key to return to the main menu..."&&echo -e "${R}"||b "Invalid Input" "Please enter a valid choice (1-3) or 0 to exit.";done
