#!/bin/bash
E='\x1b[38;5;85m';R='\033[0m';w(){ fold -s -w 78 <<<"$1"; };b(){ a=("$@");printf "${E}+";printf -- '-%.0s' $(seq 1 80);printf "+${R}\n";for l in "${a[@]}";do w "$l"|while IFS= read -r x;do printf "${E}|${R} %-78s ${E}|${R}\n" "$x";done;done;printf "${E}+";printf -- '-%.0s' $(seq 1 80);printf "+${R}\n";};d(){ clear;b "9 of Diamonds: Gadolinium Terbium and Dysprosium" "These rare earth elements are critical for medical imaging display technology and high-performance magnets." "Gadolinium is used in MRIs terbium in green phosphors and dysprosium in high-strength magnets for spacecraft.";echo;b "1. Gadolinium" "2. Terbium" "3. Dysprosium" "" "Input required: Select an element (1-3) or press 0 to exit.";};s(){ clear;b "Element Details" "Symbol: $1" "Name: $2" "Atomic Number: $3" "Atomic Weight: $4";echo;b "Description:" "$5";};e=("Gd|Gadolinium|64|157.25|Gadolinium enhances MRI imaging and is important in space medical diagnostics." "Tb|Terbium|65|158.93|Terbium supports green phosphors in efficient displays and lighting systems." "Dy|Dysprosium|66|162.50|Dysprosium powers strong magnets for motors operating under extreme conditions.");while :;do d;echo -e "${E}";read -p "Enter your choice: " c;echo -e "${R}";[[ "$c" == 0 ]]&&clear&&b "Thank you!" "Exiting the 9 of Diamonds educational card."&&break;(( c>=1 && c<=3 ))&&IFS='|' read -r y n a w m<<<"${e[$((c-1))]}"&&s "$y" "$n" "$a" "$w" "$m"&&echo -e "${E}"&&read -n1 -s -r -p "Press any key to return to the main menu..."&&echo -e "${R}"||b "Invalid Input" "Please enter a valid choice (1-3) or 0 to exit.";done
