#!/bin/bash
E='\x1b[38;5;85m';R='\033[0m';w(){ echo "$1"|fold -s -w78; };b(){ a=("$@");printf "${E}+";printf -- '-%.0s' $(seq 1 80);printf "+${R}\n";for l in "${a[@]}";do w "$l"|while IFS= read -r x;do printf "${E}|${R} %-78s ${E}|${R}\n" "$x";done;done;printf "${E}+";printf -- '-%.0s' $(seq 1 80);printf "+${R}\n";};d(){ clear;b "10 of Diamonds: Ho Holmium, Er Erbium, Tm Thulium" "These rare earth elements are used in magnetic systems, fiber optics, and advanced lasers for space applications." "Holmium is used in magnets, erbium in optical amplifiers, and thulium in laser technologies.";echo;b "1. Holmium" "2. Erbium" "3. Thulium" "" "Input required: Select an element (1-3) or press 0 to exit.";};s(){ clear;b "Element Details" "Symbol: $1" "Name: $2" "Atomic Number: $3" "Atomic Weight: $4";echo;b "Description:" "$5";};e=("Ho|Holmium|67|164.93|Holmium is used in magnetic systems due to its high magnetic strength. It plays a critical role in stabilizing spacecraft navigation and communication systems." "Er|Erbium|68|167.26|Erbium is essential for optical amplifiers in fiber optic cables. It enhances signal strength in communication systems, including those used in spacecraft." "Tm|Thulium|69|168.93|Thulium is used in advanced laser technologies, particularly for medical and scientific applications. Its properties are ideal for precision operations in space environments.");while :;do d;echo -e "${E}";read -p "Enter your choice: " c;echo -e "${R}";[[ "$c" == 0 ]]&&clear&&b "Thank you!" "Exiting the 10 of Diamonds educational card."&&break;(( c>=1 && c<=3 ))&&IFS='|' read -r y n a w m<<<"${e[$((c-1))]}"&&s "$y" "$n" "$a" "$w" "$m"&&echo -e "${E}"&&read -n1 -s -r -p "Press any key to return to the main menu..."&&echo -e "${R}"||b "Invalid Input" "Please enter a valid choice (1-3) or 0 to exit.";done
