#!/bin/bash
E='\x1b[38;5;85m';R='\033[0m';w(){ echo "$1"|fold -s -w 78; };b(){ a=("$@");printf "${E}+";printf -- '-%.0s' $(seq 1 80);printf "+${R}\n";for l in "${a[@]}";do w "$l"|while IFS= read -r x;do printf "${E}|${R} %-78s ${E}|${R}\n" "$x";done;done;printf "${E}+";printf -- '-%.0s' $(seq 1 80);printf "+${R}\n";};d(){ clear;b "Ace of Spades - Og Oganesson, La Lanthanum and Ce Cerium" "These elements span the periodic frontier. Oganesson, a synthetic noble gas, represents" "our exploration of atomic extremes. Lanthanum supports advanced optics and batteries." "Cerium is vital in catalysts for aerospace material refinement.";echo;b "1. Oganesson" "2. Lanthanum" "3. Cerium" "" "Input required: Select an element (1-3) or press 0 to exit.";};s(){ clear;b "Element Details" "Symbol: $1" "Name: $2" "Atomic Number: $3" "Atomic Weight: $4";echo;b "Description:" "$5";};e=("Og|Oganesson|118|294|Oganesson is a synthetic noble gas with theoretical importance in atomic structure studies under extreme conditions." "La|Lanthanum|57|138.91|Lanthanum is used in optics and power cells, crucial in spacecraft systems and imaging." "Ce|Cerium|58|140.12|Cerium is used in polishing agents and fuel catalysts for efficient aerospace applications.");while :;do d;echo -e "${E}";read -p "Enter your choice: " c;echo -e "${R}";[[ "$c" == 0 ]]&&clear&&b "Thank you!" "Exiting the Ace of Spades educational card."&&break;(( c>=1 && c<=3 ))&&IFS='|' read -r y n a w m<<<"${e[$((c-1))]}"&&s "$y" "$n" "$a" "$w" "$m"&&echo -e "${E}"&&read -n1 -s -r -p "Press any key to return to the main menu..."&&echo -e "${R}"||b "Invalid Input" "Please enter a valid choice (1-3) or 0 to exit.";done
