#!/bin/bash
E='\x1b[38;5;85m';R='\033[0m';w(){ echo "$1"|fold -s -w 78; };b(){ a=("$@");printf "${E}+";printf -- '-%.0s' $(seq 1 80);printf "+${R}\n";for l in "${a[@]}";do w "$l"|while IFS= read -r x;do printf "${E}|${R} %-78s ${E}|${R}\n" "$x";done;done;printf "${E}+";printf -- '-%.0s' $(seq 1 80);printf "+${R}\n";};d(){ clear;b "4 of Spades: Tb Terbium, Dy Dysprosium and Ho Holmium" "These lanthanoids are used in efficient lighting, magnets, and medical lasers. Terbium improves screen color, dysprosium enhances magnetic strength, and holmium serves in surgery and nuclear systems.";echo;b "1. Terbium (Tb)" "2. Dysprosium (Dy)" "3. Holmium (Ho)" "" "Input required: Select an element (1-3) or press 0 to exit.";};s(){ clear;b "Element Details" "Symbol: $1" "Name: $2" "Atomic Number: $3" "Atomic Weight: $4";echo;b "Description:" "$5";};e=("Tb|Terbium|65|158.93|Terbium is used in green phosphors for efficient lighting and display technologies, as well as in solid-state devices and fuel cells." "Dy|Dysprosium|66|162.5|Dysprosium improves magnet strength and thermal stability, making it essential for spacecraft motors and renewable energy systems." "Ho|Holmium|67|164.93|Holmium is used in medical lasers and nuclear control rods, valued for precision applications and neutron absorption.");while :;do d;echo -e "${E}";read -p "Enter your choice: " c;echo -e "${R}";[[ "$c" == 0 ]]&&clear&&b "Thank you!" "Exiting the 4 of Spades educational card."&&break;(( c>=1 && c<=3 ))&&IFS='|' read -r y n a w m<<<"${e[$((c-1))]}"&&s "$y" "$n" "$a" "$w" "$m"&&echo -e "${E}"&&read -n1 -s -r -p "Press any key to return to the main menu..."&&echo -e "${R}"||b "Invalid Input" "Please enter a valid choice (1-3) or 0 to exit.";done
