#!/bin/bash
E='\x1b[38;5;85m';R='\033[0m';w(){ echo "$1"|fold -s -w 78; };b(){ a=("$@");printf "${E}+";printf -- '-%.0s' $(seq 1 80);printf "+${R}\n";for l in "${a[@]}";do w "$l"|while IFS= read -r x;do printf "${E}|${R} %-78s ${E}|${R}\n" "$x";done;done;printf "${E}+";printf -- '-%.0s' $(seq 1 80);printf "+${R}\n";};d(){ clear;b "5 of Spades: Er erbium, Tm thulium and Yb ytterbium" "These lanthanoids enhance global communication and precision instrumentsâ€”erbium boosts fiber optics, thulium supports portable imaging, and ytterbium enables timekeeping and sensing.";echo;b "1. Erbium (Er)" "2. Thulium (Tm)" "3. Ytterbium (Yb)" "" "Input required: Select an element (1-3) or press 0 to exit.";};s(){ clear;b "Element Details" "Symbol: $1" "Name: $2" "Atomic Number: $3" "Atomic Weight: $4";echo;b "Description:" "$5";};e=("Er|Erbium|68|167.26|Erbium is widely used in optical fiber amplifiers for telecommunication networks. Its unique properties make it ideal for signal amplification over long distances." "Tm|Thulium|69|168.93|Thulium is employed in portable X-ray devices and laser applications. Its isotopes are valuable for medical imaging and diagnostic equipment." "Yb|Ytterbium|70|173.04|Ytterbium is used in atomic clocks and stress sensors. Its precision enables accurate timekeeping and performance monitoring in advanced systems.");while :;do d;echo -e "${E}";read -p "Enter your choice: " c;echo -e "${R}";[[ "$c" == 0 ]]&&clear&&b "Thank you!" "Exiting the 5 of Spades educational card."&&break;(( c>=1 && c<=3 ))&&IFS='|' read -r y n a w m<<<"${e[$((c-1))]}"&&s "$y" "$n" "$a" "$w" "$m"&&echo -e "${E}"&&read -n1 -s -r -p "Press any key to return to the main menu..."&&echo -e "${R}"||b "Invalid Input" "Please enter a valid choice (1-3) or 0 to exit.";done
