#!/bin/bash
E='\x1b[38;5;85m';R='\033[0m';w(){ echo "$1"|fold -s -w 78; };b(){ a=("$@");printf "${E}+";printf -- '-%.0s' $(seq 1 80);printf "+${R}\n";for l in "${a[@]}";do w "$l"|while IFS= read -r x;do printf "${E}|${R} %-78s ${E}|${R}\n" "$x";done;done;printf "${E}+";printf -- '-%.0s' $(seq 1 80);printf "+${R}\n";};d(){ clear;b "Queen of Spades: Ac Actinium, Th Thorium and Pa Protactinium" "These actinides are known for their radioactive properties and roles in nuclear research-actinium emits radiation for therapy, thorium offers potential as reactor fuel, and protactinium helps decode nuclear decay.";echo;b "1. Actinium (Ac)" "2. Thorium (Th)" "3. Protactinium (Pa)" "" "Input required: Select an element (1-3) or press 0 to exit.";};s(){ clear;b "Element Details" "Symbol: $1" "Name: $2" "Atomic Number: $3" "Atomic Weight: $4";echo;b "Description:" "$5";};e=("Ac|Actinium|89|227|Actinium is a radioactive element used as a neutron source and in radiation therapy. Its intense radioactivity makes it a valuable component in scientific research." "Th|Thorium|90|232.04|Thorium has potential as a nuclear fuel in advanced reactors due to its abundance and relatively lower waste generation. It is also used in high-quality optical lenses." "Pa|Protactinium|91|231.04|Protactinium is studied in nuclear science for its unique properties and isotopes. It plays a role in understanding nuclear reactions and decay chains.");while :;do d;echo -e "${E}";read -p "Enter your choice: " c;echo -e "${R}";[[ "$c" == 0 ]]&&clear&&b "Thank you!" "Exiting the Queen of Spades educational card."&&break;(( c>=1 && c<=3 ))&&IFS='|' read -r y n a w m<<<"${e[$((c-1))]}"&&s "$y" "$n" "$a" "$w" "$m"&&echo -e "${E}"&&read -n1 -s -r -p "Press any key to return to the main menu..."&&echo -e "${R}"||b "Invalid Input" "Please enter a valid choice (1-3) or 0 to exit.";done
