#!/bin/bash
E='\x1b[38;5;85m';R='\033[0m';w(){ echo "$1"|fold -s -w 78; };b(){ a=("$@");printf "${E}+";printf -- '-%.0s' $(seq 1 80);printf "+${R}\n";for l in "${a[@]}";do w "$l"|while IFS= read -r x;do printf "${E}|${R} %-78s ${E}|${R}\n" "$x";done;done;printf "${E}+";printf -- '-%.0s' $(seq 1 80);printf "+${R}\n";};d(){ clear;b "2 of Clubs: Pb Lead, Bi Bismuth and Po Polonium" "These heavy metals support shielding, structural integrity, and deep-space energy-lead protects from radiation, bismuth forms precision alloys, and polonium powers long missions.";echo;b "1. Lead (Pb)" "2. Bismuth (Bi)" "3. Polonium (Po)" "" "Input required: Select an element (1-3) or press 0 to exit.";};s(){ clear;b "Element Details" "Symbol: $1" "Name: $2" "Atomic Number: $3" "Atomic Weight: $4";echo;b "Description:" "$5";};e=("Pb|Lead|82|207.2|Lead is used in radiation shielding for protecting sensitive equipment and astronauts from cosmic and solar radiation during space missions." "Bi|Bismuth|83|208.98|Bismuth is used in specialized alloys for low-melting-point applications and has potential as a non-toxic substitute for lead in various systems." "Po|Polonium|84|209|Polonium is used as a heat source in radioisotope thermoelectric generators (RTGs), providing energy for spacecraft operating in deep-space missions.");while :;do d;echo -e "${E}";read -p "Enter your choice: " c;echo -e "${R}";[[ "$c" == 0 ]]&&clear&&b "Thank you!" "Exiting the 2 of Clubs educational card."&&break;(( c>=1 && c<=3 ))&&IFS='|' read -r y n a w m<<<"${e[$((c-1))]}"&&s "$y" "$n" "$a" "$w" "$m"&&echo -e "${E}"&&read -n1 -s -r -p "Press any key to return to the main menu..."&&echo -e "${R}"||b "Invalid Input" "Please enter a valid choice (1-3) or 0 to exit.";done
