#!/bin/bash
E='\x1b[38;5;85m';R='\033[0m';w(){ echo "$1"|fold -s -w 78; };b(){ a=("$@");printf "${E}+";printf -- '-%.0s' $(seq 1 80);printf "+${R}\n";for l in "${a[@]}";do w "$l"|while IFS= read -r x;do printf "${E}|${R} %-78s ${E}|${R}\n" "$x";done;done;printf "${E}+";printf -- '-%.0s' $(seq 1 80);printf "+${R}\n";};d(){ clear;b "6 of Clubs: Pu Plutonium, Am Americium and Cm Curium" "These radioactive elements support spacecraft energy, science and testing-plutonium fuels RTGs, americium aids detection, and curium analyzes alien soil.";echo;b "1. Plutonium (Pu)" "2. Americium (Am)" "3. Curium (Cm)" "" "Input required: Select an element (1-3) or press 0 to exit.";};s(){ clear;b "Element Details" "Symbol: $1" "Name: $2" "Atomic Number: $3" "Atomic Weight: $4";echo;b "Description:" "$5";};e=("Pu|Plutonium|94|244|Plutonium powers deep-space RTGs providing stable long-term energy." "Am|Americium|95|243|Americium is used in detectors and emits gamma rays for space analysis." "Cm|Curium|96|247|Curium helps analyze planets with alpha-particle spectroscopy.");while :;do d;echo -e "${E}";read -p "Enter your choice: " c;echo -e "${R}";[[ "$c" == 0 ]]&&clear&&b "Thank you!" "Exiting the 6 of Clubs educational card."&&break;(( c>=1 && c<=3 ))&&IFS='|' read -r y n a w m<<<"${e[$((c-1))]}"&&s "$y" "$n" "$a" "$w" "$m"&&echo -e "${E}"&&read -n1 -s -r -p "Press any key to return to the main menu..."&&echo -e "${R}"||b "Invalid Input" "Please enter a valid choice (1-3) or 0 to exit.";done
