#!/bin/bash
E='\x1b[38;5;85m';R='\033[0m';w(){ echo "$1"|fold -s -w 78; };b(){ a=("$@");printf "${E}+";printf -- '-%.0s' $(seq 1 80);printf "+${R}\n";for l in "${a[@]}";do w "$l"|while IFS= read -r x;do printf "${E}|${R} %-78s ${E}|${R}\n" "$x";done;done;printf "${E}+";printf -- '-%.0s' $(seq 1 80);printf "+${R}\n";};d(){ clear;b "7 of Clubs: Bk Berkelium, Cf Californium and Es Einsteinium" "These transuranic elements support nuclear research discovery and space energy-berkelium aids synthesis californium emits neutrons and einsteinium advances chemistry.";echo;b "1. Berkelium (Bk)" "2. Californium (Cf)" "3. Einsteinium (Es)" "" "Input required: Select an element (1-3) or press 0 to exit.";};s(){ clear;b "Element Details" "Symbol: $1" "Name: $2" "Atomic Number: $3" "Atomic Weight: $4";echo;b "Description:" "$5";};e=("Bk|Berkelium|97|247|Berkelium is primarily used for research into the synthesis of heavier elements. Its properties help scientists understand the behavior of actinides in nuclear reactions." "Cf|Californium|98|251|Californium is used in neutron sources for material analysis and medical applications. It plays a critical role in detecting elements and compounds in space exploration." "Es|Einsteinium|99|252|Einsteinium is produced in minute quantities for scientific research. It advances our understanding of nuclear chemistry and aids in the study of actinide elements.");while :;do d;echo -e "${E}";read -p "Enter your choice: " c;echo -e "${R}";[[ "$c" == 0 ]]&&clear&&b "Thank you!" "Exiting the 7 of Clubs educational card."&&break;(( c>=1 && c<=3 ))&&IFS='|' read -r y n a w m<<<"${e[$((c-1))]}"&&s "$y" "$n" "$a" "$w" "$m"&&echo -e "${E}"&&read -n1 -s -r -p "Press any key to return to the main menu..."&&echo -e "${R}"||b "Invalid Input" "Please enter a valid choice (1-3) or 0 to exit.";done
