#!/bin/bash
E='\x1b[38;5;85m';R='\033[0m';w(){ echo "$1"|fold -s -w 78; };b(){ a=("$@");printf "${E}+";printf -- '-%.0s' $(seq 1 80);printf "+${R}\n";for l in "${a[@]}";do w "$l"|while IFS= read -r x;do printf "${E}|${R} %-78s ${E}|${R}\n" "$x";done;done;printf "${E}+";printf -- '-%.0s' $(seq 1 80);printf "+${R}\n";};d(){ clear;b "8 of Clubs: Fm Fermium, Md Mendelevium and No Nobelium" "These heavy elements are synthesized in minute quantities and play a crucial role in advancing our understanding of nuclear physics and chemistry." "Fermium is used in research on nuclear reactions, mendelevium in chemical studies, and nobelium provides insights into actinide behavior.";echo;b "1. Fermium (Fm)" "2. Mendelevium (Md)" "3. Nobelium (No)" "" "Input required: Select an element (1-3) or press 0 to exit.";};s(){ clear;b "Element Details" "Symbol: $1" "Name: $2" "Atomic Number: $3" "Atomic Weight: $4";echo;b "Description:" "$5";};e=("Fm|Fermium|100|257|Fermium is produced in nuclear reactors and particle accelerators. It is used to study the properties of heavy elements and nuclear processes relevant to energy generation in space systems." "Md|Mendelevium|101|258|Mendelevium is synthesized for chemical research, providing valuable data on the behavior of actinides. It helps advance nuclear science for space technology." "No|Nobelium|102|259|Nobelium is a synthetic element studied for its unique chemical properties. Its research contributes to our understanding of the actinide series in advanced nuclear applications.");while :;do d;echo -e "${E}";read -p "Enter your choice: " c;echo -e "${R}";[[ "$c" == 0 ]]&&clear&&b "Thank you!" "Exiting the 8 of Clubs educational card."&&break;(( c>=1 && c<=3 ))&&IFS='|' read -r y n a w m<<<"${e[$((c-1))]}"&&s "$y" "$n" "$a" "$w" "$m"&&echo -e "${E}"&&read -n1 -s -r -p "Press any key to return to the main menu..."&&echo -e "${R}"||b "Invalid Input" "Please enter a valid choice (1-3) or 0 to exit.";done
