#!/bin/bash
E='\x1b[38;5;85m';R='\033[0m';w(){ echo "$1"|fold -s -w 78; };b(){ a=("$@");printf "${E}+";printf -- '-%.0s' $(seq 1 80);printf "+${R}\n";for l in "${a[@]}";do w "$l"|while IFS= read -r x;do printf "${E}|${R} %-78s ${E}|${R}\n" "$x";done;done;printf "${E}+";printf -- '-%.0s' $(seq 1 80);printf "+${R}\n";};d(){ clear;b "9 of Clubs: Lr Lawrencium, Rf Rutherfordium and Db Dubnium" "These superheavy elements deepen our grasp of atomic limits and heavy nuclei-lawrencium aids actinide studies, rutherfordium informs stability theory, and dubnium reveals superheavy behavior.";echo;b "1. Lawrencium (Lr)" "2. Rutherfordium (Rf)" "3. Dubnium (Db)" "" "Input required: Select an element (1-3) or press 0 to exit.";};s(){ clear;b "Element Details" "Symbol: $1" "Name: $2" "Atomic Number: $3" "Atomic Weight: $4";echo;b "Description:" "$5";};e=("Lr|Lawrencium|103|262|Lawrencium is an actinide element used in advanced research to understand electron configurations and bonding in heavy elements. It aids in refining nuclear models for space energy applications." "Rf|Rutherfordium|104|267|Rutherfordium is a superheavy element studied to explore the properties of nuclei at extreme atomic numbers. Its research provides insights into nuclear stability and reactions." "Db|Dubnium|105|270|Dubnium helps scientists investigate the chemical properties of superheavy elements. Its behavior under extreme conditions advances our understanding of nuclear and chemical science.");while :;do d;echo -e "${E}";read -p "Enter your choice: " c;echo -e "${R}";[[ "$c" == 0 ]]&&clear&&b "Thank you!" "Exiting the 9 of Clubs educational card."&&break;(( c>=1 && c<=3 ))&&IFS='|' read -r y n a w m<<<"${e[$((c-1))]}"&&s "$y" "$n" "$a" "$w" "$m"&&echo -e "${E}"&&read -n1 -s -r -p "Press any key to return to the main menu..."&&echo -e "${R}"||b "Invalid Input" "Please enter a valid choice (1-3) or 0 to exit.";done
