#!/bin/bash
E='\x1b[38;5;85m';R='\033[0m';w(){ echo "$1"|fold -s -w 78; };b(){ a=("$@");printf "${E}+";printf -- '-%.0s' $(seq 1 80);printf "+${R}\n";for l in "${a[@]}";do w "$l"|while IFS= read -r x;do printf "${E}|${R} %-78s ${E}|${R}\n" "$x";done;done;printf "${E}+";printf -- '-%.0s' $(seq 1 80);printf "+${R}\n";};d(){ clear;b "Jack of Clubs: Mt Meitnerium, Ds Darmstadtium and Rg Roentgenium" "These synthetic elements honor great scientists and fuel research into atomic structure at the limits of the periodic table.";echo;b "1. Meitnerium (Mt)" "2. Darmstadtium (Ds)" "3. Roentgenium (Rg)" "" "Input required: Select an element (1-3) or press 0 to exit.";};s(){ clear;b "Element Details" "Symbol: $1" "Name: $2" "Atomic Number: $3" "Atomic Weight: $4";echo;b "Description:" "$5";};e=("Mt|Meitnerium|109|278|Meitnerium is named after Lise Meitner and studied for its role in nuclear fission research." "Ds|Darmstadtium|110|281|Darmstadtium is named for Darmstadt, Germany, and is studied for superheavy atom stability." "Rg|Roentgenium|111|282|Roentgenium honors Wilhelm Roentgen and aids research into the behavior of heavy elements.");while :;do d;echo -e "${E}";read -p "Enter your choice: " c;echo -e "${R}";[[ "$c" == 0 ]]&&clear&&b "Thank you!" "Exiting the Jack of Clubs educational card."&&break;(( c>=1 && c<=3 ))&&IFS='|' read -r y n a w m<<<"${e[$((c-1))]}"&&s "$y" "$n" "$a" "$w" "$m"&&echo -e "${E}"&&read -n1 -s -r -p "Press any key to return to the main menu..."&&echo -e "${R}"||b "Invalid Input" "Please enter a valid choice (1-3) or 0 to exit.";done
