#!/bin/bash
E='\x1b[38;5;85m';R='\033[0m';w(){ echo "$1"|fold -s -w 78; };b(){ a=("$@");printf "${E}+";printf -- '-%.0s' $(seq 1 80);printf "+${R}\n";for l in "${a[@]}";do w "$l"|while IFS= read -r x;do printf "${E}|${R} %-78s ${E}|${R}\n" "$x";done;done;printf "${E}+";printf -- '-%.0s' $(seq 1 80);printf "+${R}\n";};d(){ clear;b "King of Clubs: Mc Moscovium, Lv Livermorium and Ts Tennessine" "These recently discovered elements extend the periodic table and showcase advances in synthesis. Moscovium honors Moscow, Livermorium recognizes Lawrence Livermore Lab, and Tennessine salutes Tennessee's role in discovery.";echo;b "1. Moscovium (Mc)" "2. Livermorium (Lv)" "3. Tennessine (Ts)" "" "Input required: Select an element (1-3) or press 0 to exit.";};s(){ clear;b "Element Details" "Symbol: $1" "Name: $2" "Atomic Number: $3" "Atomic Weight: $4";echo;b "Description:" "$5";};e=("Mc|Moscovium|115|290|Moscovium is a synthetic element named after Moscow, produced in accelerators and studied for its nuclear stability." "Lv|Livermorium|116|293|Livermorium honors Livermore Lab and supports research in superheavy synthesis and decay." "Ts|Tennessine|117|294|Tennessine, named for Tennessee, contributes to efforts probing the limits of atomic structure.");while :;do d;echo -e "${E}";read -p "Enter your choice: " c;echo -e "${R}";[[ "$c" == 0 ]]&&clear&&b "Thank you!" "Exiting the King of Clubs educational card."&&break;(( c>=1 && c<=3 ))&&IFS='|' read -r y n a w m<<<"${e[$((c-1))]}"&&s "$y" "$n" "$a" "$w" "$m"&&echo -e "${E}"&&read -n1 -s -r -p "Press any key to return to the main menu..."&&echo -e "${R}"||b "Invalid Input" "Please enter a valid choice (1-3) or 0 to exit.";done
