#!/bin/bash
E='\x1b[38;5;46m';R='\033[0m';w(){ fold -s -w 60 <<<"$1"; };b(){ a=("$@");printf "${E}+";printf -- '-%.0s' $(seq 1 60);printf "+${R}\n";for l in "${a[@]}";do w "$l"|while IFS= read -r x;do printf "${E}|${R} %-58s ${E}|${R}\n" "$x";done;done;printf "${E}+";printf -- '-%.0s' $(seq 1 60);printf "+${R}\n"; };pause(){ echo;read -n1 -s -r -p "SCAN TO CONTINUE..."; };demo(){ clear;case "$1" in A)b "GLOB DEMO: *.txt" "This wildcard matches ANY file that ends in .txt" "It's useful to operate on all text files in a folder" "Try combining with cp, mv, or cat for batch actions";ls *.txt 2>/dev/null;;B)b "GLOB DEMO: {a,b}.sh" "Brace expansion creates multiple targets: a.sh and b.sh" "Useful for scripting repeated patterns or testing alt names";ls {a,b}.sh 2>/dev/null;;C)b "GLOB DEMO: file{1..3}.log" "Brace ranges expand to file1.log file2.log file3.log" "Use when creating/test logging output patterns";ls file{1..3}.log 2>/dev/null;;D)b "GLOB DEMO: prefix*" "* matches any characters, so this finds prefixA, prefix123" "Use to scan for related grouped files";ls prefix* 2>/dev/null;;E)b "GLOB DEMO: *.{jpg,png}" "Brace with wildcards expands to *.jpg and *.png together" "Great for handling mixed image extensions";ls *.{jpg,png} 2>/dev/null;;*)b "INVALID SCAN CODE";pause;return;;esac;pause;};while true;do clear;b "BAAASH SYSTEMS INTERFACE 41386 - 5 OF SPADES" "MISSION: GLOBBING & EXPANSION" "MODE: SCANNER ONLY — NO KEYBOARD ENTRY" "OBJECTIVE: MASTER PATTERNS THAT CONTROL FILE MATCHING" "TOOLS: *, {}, .., ? — VALID FOR MOST SHELL ENVIRONMENTS";b "SCAN MENU:" "A = *.txt — Match all text files" "B = {a,b}.sh — Expand to a.sh and b.sh" "C = file{1..3}.log — Creates numbered files" "D = prefix* — Match files starting with prefix" "E = *.{jpg,png} — Match multiple extensions" "Q = Exit Module";read -p "SCAN CODE: " x;[[ "${x^^}" == Q ]]&&clear&&b "MODULE COMPLETE — 5♠ WILDCARDS MASTERED"&&break;demo "${x^^}";done
