#!/bin/bash
E='\x1b[38;5;46m';R='\033[0m';w(){ [ -n "$1" ]&&echo "$1"|fold -s -w 60||echo " ";};b(){ a=("$@");printf "${E}+";printf -- '-%.0s' $(seq 1 60);printf "+${R}\n";for l in "${a[@]}";do w "$l"|while IFS= read -r x;do printf "${E}|${R} %-58s ${E}|${R}\n" "$x";done;done;printf "${E}+";printf -- '-%.0s' $(seq 1 60);printf "+${R}\n";};pause(){ echo;read -n1 -s -r -p "SCAN TO CONTINUE...";};abs(){ clear;b "ABSOLUTE PATH EXAMPLE" "/home/user/docs/report.txt" "Starts from root ( / ), always exact" "Independent of current location" "$(pwd)";pause;};rel(){ clear;b "RELATIVE PATH EXAMPLE" "../docs/report.txt" "Based on where you are now" "Uses ./ ../ to traverse directories";pause;};nav(){ while true;do clear;b "INTERACTIVE NAVIGATION MODE" "Use scanner input to move through folders" "PWD = Show current path" "CD .. = Go up" "CD name = Enter directory" "Q = Quit navigation";mapfile -t dirs < <(find . -maxdepth 1 -type d | sed 's|^\./||');for i in "${!dirs[@]}";do printf "${E}|${R} %-2s = %-53s ${E}|${R}\n" "$i" "${dirs[$i]}";done;printf "${E}+";printf -- '-%.0s' $(seq 1 60);printf "+${R}\n";read -p "SCAN: " c;case "${c^^}" in PWD)pwd;pause;;Q)break;;..|0)cd ..;;[0-9]*)[[ -n "${dirs[$c]}" ]]&&cd "${dirs[$c]}"||b "INVALID DIRECTORY";pause;;*)b "INVALID SCAN";pause;;esac;done;};demo(){ while true;do clear;b "DEFAULT PATH DEMOS" "1 = Absolute: /usr/bin" "2 = Relative: ../bin" "3 = Combined: ../../etc" "4 = Home Shortcut: ~/Documents" "0 = Back to Menu";read -p "SCAN: " d;case "$d" in 1)clear;b "/usr/bin is absolute" "Used to find global executables";ls /usr/bin | head -n 5;pause;;2)clear;b "../bin is relative" "Moves up then down into bin";pause;;3)clear;b "cd ../../etc is relative combo" "Moves up two levels, into /etc";pause;;4)clear;b "Try: cd ~/Documents" "This uses a home shortcut";pause;;0)break;;*)b "INVALID OPTION";pause;;esac;done;};while true;do clear;b "BAAASH SYSTEMS INTERFACE 41386 - 6 OF SPADES" "MISSION: PATH TYPES - ABSOLUTE VS RELATIVE" "MODE: SCANNER ONLY - NO KEYBOARD ENTRY" "LEARN: Filesystem navigation and resolution";b "SCAN MENU" "A = What is an Absolute Path?" "B = What is a Relative Path?" "C = Navigation Mode" "D = Path Demo Menu" "Q = Exit Training Module";read -p "SCAN CODE: " x;case "${x^^}" in A)abs;;B)rel;;C)nav;;D)demo;;Q)clear;b "MODULE COMPLETE - 6 OF SPADES";break;;*)b "INVALID CODE";pause;;esac;done
