#!/bin/bash
E='\x1b[38;5;46m';R='\033[0m';w(){ [ -n "$1" ]&&echo "$1"|fold -s -w 60||echo " "; };b(){ a=("$@");printf "${E}+";printf -- '-%.0s' $(seq 1 60);printf "+${R}\n";for l in "${a[@]}";do w "$l"|while IFS= read -r x;do printf "${E}|${R} %-58s ${E}|${R}\n" "$x";done;done;printf "${E}+";printf -- '-%.0s' $(seq 1 60);printf "+${R}\n";};pause(){ echo;read -n1 -s -r -p "SCAN TO CONTINUE..."; };pipe(){ clear;b "PIPE: ps aux | grep ssh | wc -l" "Processes -> ssh filter -> count lines";ps aux|grep ssh|wc -l;pause;};redir(){ clear;b "REDIRECT: ls > out.txt" "Output of ls saved";ls>out.txt;echo "Created out.txt";pause;};append(){ clear;b "APPEND: date >> out.txt" "Add time to end of out.txt";date>>out.txt;pause;};input(){ clear;b "INPUT: sort < out.txt" "Sort content of out.txt";sort<out.txt;pause;};view(){ clear;b "VIEW out.txt";cat out.txt 2>/dev/null||echo "No file.";pause;};while true;do clear;b "BAAASH SYSTEMS INTERFACE 41386 - 8 OF SPADES" "MISSION: PIPES & REDIRECTS" "MODE: SCANNER ONLY - NO KEYBOARD ENTRY" "TOOLS: | > >> <";b "A = Pipe Example" "B = Redirect >" "C = Append >>" "D = Input <" "E = View File" "Q = Exit";read -p "SCAN CODE: " x;case "${x^^}" in A)pipe;;B)redir;;C)append;;D)input;;E)view;;Q)clear;b "MODULE COMPLETE";break;;*)b "INVALID SCAN";pause;;esac;done
