#!/bin/bash
var1=""; var2=""; log_file="./session.log"; header(){ clear; echo "=============================================================="; echo "                 VARIABLE MANAGEMENT CONSOLE"; echo "--------------------------------------------------------------"; echo "   1 SCREEN · 1 INPUT · 1 TASK · 1 PURPOSE · 1 EXIT"; echo "   2 VARIABLES · 2 STATES (SET/UNSET) · 2 FILE OPERATIONS"; echo "   10 KEYS OR LESS TO MASTER · RULE OF TEN COMPLIANT"; echo "--------------------------------------------------------------"; echo " Timestamp: $(date +"%Y-%m-%d %H:%M:%S")  Host: $(hostname)"; echo "=============================================================="; }; menu(){ echo "[1] Set Variables        → Define both values"; echo "[2] View Variables       → Display current state"; echo "[3] Update Variables     → Modify one or both"; echo "[4] Reset Variables      → Clear all values"; echo "[5] Save to Log          → Store session values"; echo "[6] Load from Log        → Restore from file"; echo "[7] Self-Test Check      → Verify runtime state"; echo "[8] Exit Program         → Terminate interface"; echo "--------------------------------------------------------------"; }; pause(){ echo; read -p "↩ Press Enter to continue..." dummy; }; set_variables(){ header; echo "[ SET MODE ] Input values:"; read -p " → Variable 1: " input1; read -p " → Variable 2: " input2; [[ -n "$input1" && -n "$input2" ]] && var1="$input1" && var2="$input2" && echo "✔ Values stored." || echo "✖ Input error. Retry."; pause; }; print_variables(){ header; echo "[ VIEW MODE ]"; echo " → Variable 1: ${var1:-<unset>}"; echo " → Variable 2: ${var2:-<unset>}"; pause; }; update_variables(){ header; echo "[ UPDATE MODE ]"; read -p " Update Variable 1? (y/n): " u1; [[ "$u1" =~ ^[Yy]$ ]] && read -p " → New value: " v1 && [[ -n "$v1" ]] && var1="$v1"; read -p " Update Variable 2? (y/n): " u2; [[ "$u2" =~ ^[Yy]$ ]] && read -p " → New value: " v2 && [[ -n "$v2" ]] && var2="$v2"; echo "✔ Update complete."; pause; }; reset_variables(){ header; echo "[ RESET MODE ]"; read -p " Type RESET to confirm: " confirm; [[ "$confirm" == "RESET" ]] && var1="" && var2="" && echo "✔ Values cleared." || echo "✖ Reset cancelled."; pause; }; export_log(){ header; echo "[ EXPORT MODE ]"; { echo "# Session Log"; echo "Timestamp=$(date +%F_%T)"; echo "Variable_1=$var1"; echo "Variable_2=$var2"; } > "$log_file"; echo "✔ Saved to $log_file"; pause; }; import_log(){ header; echo "[ IMPORT MODE ]"; if [[ -f "$log_file" ]]; then var1=$(grep 'Variable_1=' "$log_file" | cut -d '=' -f2-); var2=$(grep 'Variable_2=' "$log_file" | cut -d '=' -f2-); echo "✔ Restored from log."; else echo "✖ Log file not found."; fi; pause; }; self_test(){ header; echo "[ SELF-TEST MODE ]"; echo " • Memory: Variable 1 (${#var1} chars), Variable 2 (${#var2} chars)"; echo " • Log Path: $log_file"; echo -n " • Log Writable: "; [[ -w . ]] && echo "YES" || echo "NO"; echo " • Shell: $SHELL"; pause; }; while true; do header; menu; echo; read -p "Select [1–8]: " choice; case "$choice" in 1) set_variables ;; 2) print_variables ;; 3) update_variables ;; 4) reset_variables ;; 5) export_log ;; 6) import_log ;; 7) self_test ;; 8) header; echo "Exit confirmed. Session complete."; exit ;; *) echo "✖ Invalid input."; pause ;; esac; done
