#!/bin/bash
while true; do clear; echo "====================== VARIABLE TRAINING TERMINAL ======================"; echo "[1] Declare Variable   → Learn how to create a variable using 'declare'"; echo "[2] Access Variable    → Learn how to access a variable using indirect expansion"; echo "[3] Update Variable    → Learn how to change a variable using 'eval'"; echo "[4] Print Variable     → Learn multiple ways to output a variable's value"; echo "[5] Exit Program       → Quit the session"; echo "======================================================================="; read -p "Select an option (1–5): " choice; case $choice in 1) read -p "Enter the variable name to declare: " name; read -p "Enter the value to assign: " value; declare "$name=$value"; echo; echo "✔ DECLARED: declare $name=$value"; echo "▶ The 'declare' command defines a variable with a specific name and value. It ensures the variable is registered in the current shell session."; sleep 5;; 2) read -p "Enter the variable name to access: " name; echo; echo "✔ ACCESS COMMAND: \${!$name}"; echo "▶ This uses indirect expansion, meaning it evaluates the name stored in the variable to access its value dynamically."; sleep 5;; 3) read -p "Enter the variable name to update: " name; read -p "Enter the new value: " value; eval "$name=$value"; echo; echo "✔ UPDATED: eval $name=$value"; echo "▶ 'eval' reconstructs and executes the variable assignment at runtime, allowing dynamic updates of named variables."; sleep 5;; 4) read -p "Enter the variable name to print: " name; echo; echo "✔ PRINT COMMANDS: echo \${$name}, echo \${!name}"; echo "▶ These display the current value of the variable. \${!name} performs indirect expansion; \${var} shows direct content."; echo "CURRENT VALUE: ${!name}"; sleep 5;; 5) clear; echo "✓ Session complete. Goodbye."; break;; *) echo "✖ Invalid option. Please choose 1 to 5."; sleep 2;; esac; done
