#!/bin/bash
 c='\033[1;36m'; r='\033[0m'; f(){ while true; do clear; echo -e "$c"; echo " __________________________________________________________"; echo "|                        Ace of Hearts                     |"; echo "|              Access Incident Response Tools              |"; echo "|__________________________________________________________|"; echo "|                                                          |"; echo "|  Select an incident response tool to use:                |"; echo "|                                                          |"; echo "|  1. Search authentication attempts                       |"; echo "|  2. Monitor active network connections                   |"; echo "|  3. Inspect running services                             |"; echo "|  4. View failed login attempts                           |"; echo "|  5. Analyze disk usage                                   |"; echo "|  6. List active users and processes                      |"; echo "|  7. Detect large files over 1GB                          |"; echo "|  8. Exit                                                 |"; echo "|__________________________________________________________|"; echo "|                                                          |"; echo "|                 Enter your choice:                       |"; echo "|__________________________________________________________|"; echo -e "$r"; read -p "Choice: " o; case $o in 1) clear; echo -e "${c}Searching for authentication logs...$r"; l=("/var/log/auth.log" "/var/log/secure" "/var/log/messages" "/var/log/syslog"); f=""; for g in "${l[@]}"; do if [ -f "$g" ]; then f="$g"; break; fi; done; if [ -n "$f" ]; then echo -e "${c}Displaying authentication attempts from $f:$r"; tail -n 50 "$f" | grep -i "auth" | less; else echo -e "${c}No authentication logs found on this system.$r"; fi;; 2) clear; echo -e "${c}Monitoring active network connections.$r"; ss -tuln | less;; 3) clear; echo -e "${c}Inspecting running services...$r"; systemctl list-units --type=service --state=running | less;; 4) clear; if [ -f /var/log/auth.log ]; then echo -e "${c}Viewing failed login attempts from /var/log/auth.log...$r"; grep -i "FAILED" /var/log/auth.log | tail -n 20 | less; elif [ -f /var/log/secure ]; then echo -e "${c}Viewing failed login attempts from /var/log/secure...$r"; grep -i "FAILED" /var/log/secure | tail -n 20 | less; else echo -e "${c}Failed login attempts log not found.$r"; fi;; 5) clear; echo -e "${c}Analyzing disk usage...$r"; df -h | less;; 6) clear; echo -e "${c}Listing active users and processes...$r"; w || echo -e "${c}No active users or processes found.$r";; 7) clear; echo -e "${c}Detecting large files (>1GB)..This may take some time.$r"; find / -type f -size +1G 2>/dev/null | less || echo -e "${c}No large files detected.$r";; 8) clear; echo -e "${c}Exiting...$r"; exit 0;; *) echo -e "${c}Invalid. Try again.$r";; esac; echo -e "${c}Scan any to return to the main menu..$r"; read -n 1 -s -r; done; }; f
