echo "Hello World"#!/bin/bash;
gr=$(tput setaf 2); rd=$(tput setaf 1); yl=$(tput setaf 3); rs=$(tput sgr0); check_internet() { wget -q --spider http://google.com; if [ $? -eq 0 ]; then echo -e "${gr}Internet connected.${rs}"; else echo -e "${rd}No internet connection.${rs}"; exit 1; fi; }; stop_service() { local service=$1; echo -e "${yl}Stopping ${service}...${rs}"; sudo systemctl stop $service; if [ $? -eq 0 ]; then echo -e "${gr}${service} stopped successfully.${rs}"; else echo -e "${rd}Failed to stop ${service}.${rs}"; fi; }; disable_service() { local service=$1; echo -e "${yl}Disabling ${service}...${rs}"; sudo systemctl disable $service; if [ $? -eq 0 ]; then echo -e "${gr}${service} disabled successfully.${rs}"; else echo -e "${rd}Failed to disable ${service}.${rs}"; fi; }; install_package() { local package=$1; echo -e "${yl}Installing ${package}...${rs}"; sudo apt-get install -y $package; if [ $? -eq 0 ]; then echo -e "${gr}${package} installed successfully.${rs}"; else echo -e "${rd}Failed to install ${package}.${rs}"; exit 1; fi; }; start_vnc() { local geometry=$1; echo -e "${yl}Starting TightVNC server with geometry ${geometry}...${rs}"; tightvncserver -geometry $geometry; if [ $? -eq 0 ]; then echo -e "${gr}TightVNC server started successfully.${rs}"; else echo -e "${rd}Failed to start TightVNC server.${rs}"; exit 1; fi; }; check_internet; stop_service "vncserver-x11-serviced.service"; stop_service "vncserver-virtuald.service"; disable_service "vncserver-virtuald.service"; install_package "tightvncserver"; echo -e "${yl}Enter VNC password:${rs}"; vncpasswd; start_vnc "1280x720"; echo -e "${gr}To connect to the VNC server, use the following details in your VNC viewer:${rs}"; echo -e "${yl}Server: $(hostname -I | awk '{print $1}'):1${rs}"
