#!/bin/bash
RED="\033[0;31m"; GREEN="\033[0;32m"; YELLOW="\033[0;33m"; CYAN="\033[0;36m"; MAGENTA="\033[0;35m"; BLINK="\033[5m"; RESET="\033[0m"; BOLD="\033[1m"; clear; echo -e "${BLINK}${CYAN}${BOLD}+---------------------------------------------------+${RESET}"; echo -e "${BLINK}${CYAN}|                ${YELLOW}Network Interfaces       ${CYAN}          |${RESET}"; echo -e "${BLINK}${CYAN}+---------------------------------------------------+${RESET}"; echo -e "${GREEN}Scan any to continue with the network scan or 'q' to quit.${RESET}"; read -r -n 1 -s key; if [[ "$key" == "q" ]]; then echo -e "${RED}\nScan aborted by the user.${RESET}"; exit 0; else clear; echo -e "${GREEN}Continuing with the scan...${RESET}"; fi; if [ "$(id -u)" -ne 0 ]; then echo -e "${RED}This script must be run as root to perform network scans.${RESET}"; exit 1; fi; echo -e "${CYAN}Enter an IP address to scan (e.g., 192.168.0.0/24), or Scan any to Continue, to scan all local networks:${RESET}"; read -r network; if [ -z "$network" ]; then network="192.168.0.0/24"; echo -e "${YELLOW}No input detected. Scanning local networks, $network${RESET}. This will take Several minutes"; fi; echo -e "${MAGENTA}Scanning $network...${RESET}"; nmap -sS -A $network | tee /tmp/nmap_scan_results.txt | grep "Nmap done" -A 1000; echo -e "${CYAN}Do you want to save the scan results? Scan (y/n):${RESET}"; read -r save; if [[ "$save" == "y" || "$save" == "Y" ]]; then touch ~/final_nmap_scan_results.txt; grep "Nmap done" /tmp/nmap_scan_results.txt -A 1000 >> ~/final_nmap_scan_results.txt; echo -e "${GREEN}Results saved to ~/final_nmap_scan_results.txt${RESET}"; echo -e "${CYAN}Do you want to view the contents of the saved file? (y/n):${RESET}"; read -r view; if [[ "$view" == "y" || "$view" == "Y" ]]; then less ~/final_nmap_scan_results.txt; else echo -e "${YELLOW}Not viewing the file.${RESET}"; fi; else echo -e "${YELLOW}Results not saved.${RESET}"; fi; echo -e "${BLINK}${CYAN}${BOLD}+---------------------------------------------------+${RESET}"; echo -e "${BLINK}${CYAN}|           ${YELLOW}End of Network Configuration${CYAN}            |${RESET}"; echo -e "${BLINK}${CYAN}+---------------------------------------------------+${RESET}"; echo -e "${GREEN}Operation completed.${RESET}"; echo -e "${CYAN}Scan any to clear the screen and finish...${RESET}"; read -r -n 1 -s; clear; echo -e "${RESET}"
