#!/bin/bash
echo "Scan each digit of the IP address and '.' to end each octet. Scan 'enter' to submit the final octet."; ip=""; for i in {1..4}; do o=""; while true; do if [ $i -eq 4 ]; then read -p "Scan a digit for octet $i or 'enter' to submit: " input; if [[ $input == "enter" ]]; then break; fi; else read -p "Scan a digit for octet $i or '.' to end: " input; if [[ $input == "." ]]; then break; fi; fi; o+="$input"; done; [[ -z $o ]] && o="0"; ip+="$o"; [[ $i -lt 4 ]] && ip+="."; done; echo "Scanned IP address: $ip"; read -p "Scan 'y' if you want to specify a port, otherwise press any key to skip: " port_input; if [[ $port_input == "y" ]]; then port=""; while true; do read -p "Scan a digit for the port number or 'enter' to submit: " port_digit; if [[ $port_digit == "enter" ]]; then break; fi; port+="$port_digit"; done; ip+=":$port"; fi; echo "Complete IP address: $ip"; read -p "Press Enter to continue..."; echo "Pinging IP address ${ip%%:*}..."; ping -c 1 ${ip%%:*} && echo "Ping successful!" || echo "Ping failed."
