#!/bin/bash
E='\x1b[38;5;85m';R='\033[0m';w(){ fold -s -w 78 <<<"$1"; };b(){ a=("$@");printf "${E}+";printf -- '-%.0s' $(seq 1 80);printf "+${R}\n";for l in "${a[@]}";do w "$l"|while IFS= read -r x;do printf "${E}|${R} %-78s ${E}|${R}\n" "$x";done;done;printf "${E}+";printf -- '-%.0s' $(seq 1 80);printf "+${R}\n";};d(){ clear;b "7 of Diamonds: (Ce: Cerium, Pr: Praseodymium, Nd: Neodymium)" "These rare earth elements are critical for magnets, alloys, and catalysts in advanced space systems." "Cerium is used in polishing agents, praseodymium in high-strength alloys, and neodymium in powerful magnets.";echo;b "1. Cerium (Ce)" "2. Praseodymium (Pr)" "3. Neodymium (Nd)" "" "Input required: Select an element (1-3) or press 0 to exit.";};s(){ clear;b "Element Details" "Symbol: $1" "Name: $2" "Atomic Number: $3" "Atomic Weight: $4";echo;b "Description:" "$5";};e=("Ce|Cerium|58|140.12|Cerium is used in polishing agents for precision optics in telescopes and cameras." "Pr|Praseodymium|59|140.91|Praseodymium strengthens alloys and enhances magnetic systems in spacecraft." "Nd|Neodymium|60|144.24|Neodymium powers compact spacecraft motors with strong, efficient magnets.");while :;do d;echo -e "${E}";read -p "Enter your choice: " c;echo -e "${R}";[[ "$c" == 0 ]]&&clear&&b "Thank you!" "Exiting the 7 of Diamonds educational card."&&break;(( c>=1 && c<=3 ))&&IFS='|' read -r y n a w m<<<"${e[$((c-1))]}"&&s "$y" "$n" "$a" "$w" "$m"&&echo -e "${E}"&&read -n1 -s -r -p "Press any key to return to the main menu..."&&echo -e "${R}"||b "Invalid Input" "Please enter a valid choice (1-3) or 0 to exit.";done
