#!/bin/bash
E='\x1b[38;5;85m';R='\033[0m';W(){ echo "$1"|fold -s -w 78;};B(){ L=80;printf "${E}+";printf -- '-%.0s' $(seq 1 $L);printf "+$R\n";for l in "$@";do W "$l"|while read -r w;do printf "${E}|${R} %-78s ${E}|${R}\n" "$w";done;done;printf "${E}+";printf -- '-%.0s' $(seq 1 $L);printf "+$R\n";};D=("Be|Beryllium|4|9.0122|Beryllium is a lightweight, high-strength metal used in aerospace materials and heat shields. It is ideal for constructing spacecraft due to its stiffness and ability to dissipate heat efficiently." "B|Boron|5|10.81|Boron is a metalloid used in composite materials and lightweight structures. It strengthens materials and provides heat resistance, making it crucial for spacecraft construction." "C|Carbon|6|12.01|Carbon is the backbone of organic life and a critical component in lightweight materials like carbon fiber. It is used extensively in spacecraft to reduce weight while maintaining strength.");while :;do clear;B "2 of Hearts: (Be: Beryllium, B: Boron, C: Carbon)" "These elements are crucial for materials science and life support systems in space." "Beryllium is used in aerospace materials, boron in composites, and carbon is the basis of organic life and structural components in spacecraft.";echo;B "1. Beryllium (Be)" "2. Boron (B)" "3. Carbon (C)" "" "Input required: Select an element (1-3) or press 0 to exit.";echo -e "$E";read -p "Enter your choice: " C;echo -e "$R";[[ $C == 0 ]]&&{ clear;B "Thank you!" "Exiting the 2 of Hearts educational card.";break;};((C>=1&&C<=3))&&{ IFS='|' read -r SY NM AN WT DESC <<<"${D[$((C-1))]}";clear;B "Element Details" "Symbol: $SY" "Name: $NM" "Atomic Number: $AN" "Atomic Weight: $WT";echo;B "Description:" "$DESC";echo -e "$E";read -n 1 -s -r -p "Press any key to return to the main menu...";echo -e "$R";}||{ B "Invalid Input" "Please enter a valid choice (1-3) or 0 to exit.";sleep 2;};done
