#!/bin/bash
E='\x1b[38;5;85m';R='\033[0m';W(){ echo "$1"|fold -s -w 78; };B(){ C=("$@");w=80;printf "${E}+";printf -- '-%.0s' $(seq 1 $w);printf "+${R}\n";for l in "${C[@]}";do W "$l"|while IFS= read -r x;do printf "${E}|${R} %-78s ${E}|${R}\n" "$x";done;done;printf "${E}+";printf -- '-%.0s' $(seq 1 $w);printf "+${R}\n";};D(){ clear;B "6 of Hearts: (S: Sulfur, Cl: Chlorine, Ar: Argon)" "These elements are vital for chemical reactions, sterilization, and inert atmospheres in space." "Sulfur is used in fuel, chlorine in water purification, and argon as an inert gas for welding and atmosphere control.";echo;B "1. Sulfur (S)" "2. Chlorine (Cl)" "3. Argon (Ar)" "" "Input required: Select an element (1-3) or press 0 to exit.";};ELEM=( "S|Sulfur|16|32.06|Sulfur is used in chemical propellants and as a component in fuels. Its compounds are essential for various reactions in space exploration, including the generation of heat and propulsion." "Cl|Chlorine|17|35.45|Chlorine is a key element in water purification systems aboard spacecraft. It ensures astronauts have access to clean, safe drinking water during missions." "Ar|Argon|18|39.95|Argon is an inert gas used for welding in spacecraft assembly and maintenance. It is also employed in creating stable, non-reactive atmospheres in controlled environments." );S(){ s="$1";n="$2";z="$3";w="$4";d="$5";clear;B "Element Details" "Symbol: $s" "Name: $n" "Atomic Number: $z" "Atomic Weight: $w";echo;B "Description:" "$d";};while true;do D;echo -e "${E}";read -p "Enter your choice: " c;echo -e "${R}";if [[ "$c" == "0" ]];then clear;B "Thank you!" "Exiting the 6 of Hearts educational card.";break;elif (( c>=1 && c<=3 ));then IFS='|' read -r s n z w d <<<"${ELEM[$((c-1))]}";S "$s" "$n" "$z" "$w" "$d";echo -e "${E}";read -n 1 -s -r -p "Press any key to return to the main menu...";echo -e "${R}";else B "Invalid Input" "Please enter a valid choice (1-3) or 0 to exit.";fi;done
