#!/bin/bash
E='\x1b[38;5;85m';R='\033[0m';w(){ fold -s -w 78 <<<"$1"; };b(){ a=("$@");printf "${E}+";printf -- '-%.0s' $(seq 1 80);printf "+${R}\n";for l in "${a[@]}";do w "$l"|while IFS= read -r x;do printf "${E}|${R} %-78s ${E}|${R}\n" "$x";done;done;printf "${E}+";printf -- '-%.0s' $(seq 1 80);printf "+${R}\n";};d(){ clear;b "2 of Diamonds: (Tc: Technetium, Ru: Ruthenium, Rh: Rhodium)" "These elements are essential for medical imaging, catalysts, and corrosion-resistant materials in space." "Technetium is used in diagnostics, ruthenium in electronics, and rhodium for protective coatings.";echo;b "1. Technetium (Tc)" "2. Ruthenium (Ru)" "3. Rhodium (Rh)" "" "Input required: Select an element (1-3) or press 0 to exit.";};s(){ clear;b "Element Details" "Symbol: $1" "Name: $2" "Atomic Number: $3" "Atomic Weight: $4";echo;b "Description:" "$5";};e=("Tc|Technetium|43|98|Technetium is widely used in medical imaging and diagnostics, particularly in radioisotope tracers for monitoring biological functions. Its stability and radioactive properties make it valuable in space medicine." "Ru|Ruthenium|44|101.07|Ruthenium is used in electronics and as a catalyst in chemical reactions. It enhances the efficiency of electrical systems in spacecraft and plays a role in fuel cell technologies." "Rh|Rhodium|45|102.91|Rhodium is a highly reflective and corrosion-resistant metal used in protective coatings. It ensures longevity and durability of spacecraft components exposed to harsh space environments.");while :;do d;echo -e "${E}";read -p "Enter your choice: " c;echo -e "${R}";[[ "$c" == 0 ]]&&clear&&b "Thank you!" "Exiting the 2 of Diamonds educational card."&&break;(( c>=1 && c<=3 ))&&IFS='|' read -r y n a w m<<<"${e[$((c-1))]}"&&s "$y" "$n" "$a" "$w" "$m"&&echo -e "${E}"&&read -n1 -s -r -p "Press any key to return to the main menu..."&&echo -e "${R}"||b "Invalid Input" "Please enter a valid choice (1-3) or 0 to exit.";done
