#!/bin/bash
E='\x1b[38;5;85m';R='\033[0m';w(){ fold -s -w 78 <<<"$1"; };b(){ a=("$@");printf "${E}+";printf -- '-%.0s' $(seq 1 80);printf "+${R}\n";for l in "${a[@]}";do w "$l"|while IFS= read -r x;do printf "${E}|${R} %-78s ${E}|${R}\n" "$x";done;done;printf "${E}+";printf -- '-%.0s' $(seq 1 80);printf "+${R}\n";};d(){ clear;b "3 of Diamonds: (Pd: Palladium, Ag: Silver, Cd: Cadmium)" "These elements are critical for advanced electronics, high-efficiency catalysts, and battery technologies." "Palladium is used in fuel cells, silver in conductors, and cadmium in rechargeable batteries.";echo;b "1. Palladium (Pd)" "2. Silver (Ag)" "3. Cadmium (Cd)" "" "Input required: Select an element (1-3) or press 0 to exit.";};s(){ clear;b "Element Details" "Symbol: $1" "Name: $2" "Atomic Number: $3" "Atomic Weight: $4";echo;b "Description:" "$5";};e=("Pd|Palladium|46|106.42|Palladium is widely used in fuel cells for clean energy generation. Its catalytic properties make it ideal for converting chemical energy into electricity in space systems." "Ag|Silver|47|107.87|Silver is an excellent conductor of electricity and is used in spacecraft wiring and communication systems. Its antimicrobial properties are also utilized in water purification." "Cd|Cadmium|48|112.41|Cadmium is used in rechargeable batteries, providing reliable power storage for spacecraft. Its role in energy systems ensures efficiency and durability during long missions.");while :;do d;echo -e "${E}";read -p "Enter your choice: " c;echo -e "${R}";[[ "$c" == 0 ]]&&clear&&b "Thank you!" "Exiting the 3 of Diamonds educational card."&&break;(( c>=1 && c<=3 ))&&IFS='|' read -r y n a w m<<<"${e[$((c-1))]}"&&s "$y" "$n" "$a" "$w" "$m"&&echo -e "${E}"&&read -n1 -s -r -p "Press any key to return to the main menu..."&&echo -e "${R}"||b "Invalid Input" "Please enter a valid choice (1-3) or 0 to exit.";done
