#!/bin/bash
E='\x1b[38;5;85m';R='\033[0m';w(){ echo "$1"|fold -s -w 78; };b(){ a=("$@");printf "${E}+";printf -- '-%.0s' $(seq 1 80);printf "+${R}\n";for l in "${a[@]}";do w "$l"|while IFS= read -r x;do printf "${E}|${R} %-78s ${E}|${R}\n" "$x";done;done;printf "${E}+";printf -- '-%.0s' $(seq 1 80);printf "+${R}\n";};d(){ clear;b "Jack of Diamonds: Ytterbium Lutetium and Hafnium" "Used in atomic clocks catalysts and high-temp alloys for space systems" "Ytterbium enables precision timing Lutetium aids diagnostics Hafnium forms spacecraft shields";echo;b "1. Ytterbium" "2. Lutetium" "3. Hafnium" "" "Input required: Select an element (1-3) or press 0 to exit.";};s(){ clear;b "Element Details" "Symbol: $1" "Name: $2" "Atomic Number: $3" "Atomic Weight: $4";echo;b "Description:" "$5";};e=("Yb|Ytterbium|70|173.04|Ytterbium is used in atomic clocks for precision timekeeping crucial for spacecraft navigation and synchronization during missions." "Lu|Lutetium|71|174.97|Lutetium is employed in PET scan detectors enhancing medical diagnostics in space. It also plays a role in high-tech applications requiring stability." "Hf|Hafnium|72|178.49|Hafnium is used in heat-resistant alloys for spacecraft and nuclear control rods. Its properties make it essential for operating in extreme space environments.");while :;do d;echo -e "${E}";read -p "Enter your choice: " c;echo -e "${R}";[[ "$c" == 0 ]]&&clear&&b "Thank you!" "Exiting the Jack of Diamonds educational card."&&break;(( c>=1 && c<=3 ))&&IFS='|' read -r y n a w m<<<"${e[$((c-1))]}"&&s "$y" "$n" "$a" "$w" "$m"&&echo -e "${E}"&&read -n1 -s -r -p "Press any key to return to the main menu..."&&echo -e "${R}"||b "Invalid Input" "Please enter a valid choice (1-3) or 0 to exit.";done
