#!/bin/bash
E='\x1b[38;5;85m';R='\033[0m';w(){ echo "$1" | fold -s -w 78; };b(){ a=("$@");printf "${E}+";printf -- '-%.0s' $(seq 1 80);printf "+${R}\n";for l in "${a[@]}";do w "$l"|while IFS= read -r x;do printf "${E}|${R} %-78s ${E}|${R}\n" "$x";done;done;printf "${E}+";printf -- '-%.0s' $(seq 1 80);printf "+${R}\n";};d(){ clear;b "Queen of Diamonds: Ta Tantalum and W Tungsten and Re Rhenium" "These elements support spacecraft electronics heat shielding and propulsion systems." "Tantalum is used in capacitors tungsten in heat protection and rhenium in jet engines.";echo;b "1. Tantalum" "2. Tungsten" "3. Rhenium" "" "Input required: Select an element (1-3) or press 0 to exit.";};s(){ clear;b "Element Details" "Symbol: $1" "Name: $2" "Atomic Number: $3" "Atomic Weight: $4";echo;b "Description:" "$5";};e=("Ta|Tantalum|73|180.95|Tantalum is used in capacitors and resistors for spacecraft electronics. Its corrosion resistance ensures durability in extreme conditions." "W|Tungsten|74|183.84|Tungsten is used in heat shields and high-temp components. Its melting point protects spacecraft parts from extreme heat." "Re|Rhenium|75|186.21|Rhenium strengthens turbine blades in propulsion systems. Its temperature resilience ensures longevity.");while :;do d;echo -e "${E}";read -p "Enter your choice: " c;echo -e "${R}";[[ "$c" == 0 ]]&&clear&&b "Thank you!" "Exiting the Queen of Diamonds educational card."&&break;(( c>=1 && c<=3 ))&&IFS='|' read -r y n a w m<<<"${e[$((c-1))]}"&&s "$y" "$n" "$a" "$w" "$m"&&echo -e "${E}"&&read -n1 -s -r -p "Press any key to return to the main menu..."&&echo -e "${R}"||b "Invalid Input" "Please enter a valid choice (1-3) or 0 to exit.";done
