#!/bin/bash
E='\x1b[38;5;85m';R='\033[0m';w(){ echo "$1"|fold -s -w 78; };b(){ a=("$@");printf "${E}+";printf -- '-%.0s' $(seq 1 80);printf "+${R}\n";for l in "${a[@]}";do w "$l"|while read -r x;do printf "${E}|${R} %-78s ${E}|${R}\n" "$x";done;done;printf "${E}+";printf -- '-%.0s' $(seq 1 80);printf "+${R}\n";};d(){ clear;b "3 of Spades: Sm Samarium, Eu Europium and Gd Gadolinium" "These lanthanoids are indispensable in high-tech applications from nuclear reactors to magnetic materials. Samarium is used in powerful magnets, europium lights up screens, and gadolinium supports imaging and reactor control.";echo;b "1. Samarium (Sm)" "2. Europium (Eu)" "3. Gadolinium (Gd)" "" "Input required: Select an element (1-3) or press 0 to exit.";};s(){ clear;b "Element Details" "Symbol: $1" "Name: $2" "Atomic Number: $3" "Atomic Weight: $4";echo;b "Description:" "$5";};e=("Sm|Samarium|62|150.36|Samarium is used in samarium-cobalt magnets that resist demagnetization and are critical in spacecraft and precision gear." "Eu|Europium|63|151.96|Europium powers LED displays and lights with vivid colors making it key to space visual systems." "Gd|Gadolinium|64|157.25|Gadolinium absorbs neutrons making it ideal for nuclear control rods and is also used in MRI scans.");while :;do d;echo -e "${E}";read -p "Enter your choice: " c;echo -e "${R}";[[ "$c" == 0 ]]&&clear&&b "Thank you!" "Exiting the 3 of Spades educational card."&&break;(( c>=1 && c<=3 ))&&IFS='|' read -r y n a w m<<<"${e[$((c-1))]}"&&s "$y" "$n" "$a" "$w" "$m"&&echo -e "${E}"&&read -n1 -s -r -p "Press any key to return to the main menu..."&&echo -e "${R}"||b "Invalid Input" "Please enter a valid choice (1-3) or 0 to exit.";done
