#!/bin/bash
E='\x1b[38;5;85m';R='\033[0m';w(){ echo "$1"|sed 's/â€”/-/g'|fold -s -w 78|while IFS= read -r l;do printf "${E}|${R} %-78s ${E}|${R}\n" "$l";done;};b(){ printf "${E}+";printf -- '-%.0s' $(seq 1 80);printf "+${R}\n";for l in "$@";do w "$l";done;printf "${E}+";printf -- '-%.0s' $(seq 1 80);printf "+${R}\n";};d(){ clear;b "Ace of Clubs: Au Gold, Hg Mercury and Tl Thallium" "These elements are essential for space electronics and optics - gold conducts flawlessly, mercury enables precise measurements, and thallium improves imaging systems.";echo;b "1. Gold" "2. Mercury" "3. Thallium" "" "Input required: Select an element (1-3) or press 0 to exit.";};s(){ clear;b "Element Details" "Symbol: $1" "Name: $2" "Atomic Number: $3" "Atomic Weight: $4";echo;b "Description:" "$5";};e=("Au|Gold|79|196.97|Gold is used extensively in spacecraft electronics and circuitry due to its excellent conductivity and resistance to corrosion. It is also used for thermal shielding." "Hg|Mercury|80|200.59|Mercury is used in scientific instruments such as thermometers and barometers. In space, its applications are limited but valuable for precise measurements." "Tl|Thallium|81|204.38|Thallium is used in optical systems and radiation detection equipment. It enhances the accuracy and reliability of imaging in space exploration.");while :;do d;echo -e "${E}";read -p "Enter your choice: " c;echo -e "${R}";[[ "$c" == 0 ]]&&clear&&b "Thank you!" "Exiting the Ace of Clubs educational card."&&break;(( c>=1&&c<=3 ))&&IFS='|' read -r y n a w m<<<"${e[$((c-1))]}"&&s "$y" "$n" "$a" "$w" "$m"&&echo -e "${E}"&&read -n1 -s -r -p "Press any key to return to the main menu..."&&echo -e "${R}"||b "Invalid Input" "Please enter a valid choice (1-3) or 0 to exit.";done
