#!/bin/bash
E='\x1b[38;5;85m';R='\033[0m';w(){ echo "$1"|fold -sw 78; };b(){ a=("$@");printf "${E}+";printf -- '-%.0s' $(seq 1 80);printf "+${R}\n";for l in "${a[@]}";do w "$l"|while IFS= read -r x;do printf "${E}|${R} %-78s ${E}|${R}\n" "$x";done;done;printf "${E}+";printf -- '-%.0s' $(seq 1 80);printf "+${R}\n";};d(){ clear;b "6 of Spades: Lu lutetium, Hf hafnium and Ta tantalum" "These metals support advanced diagnostics, energy systems, and extreme condition engineering lutetium boosts scans, hafnium manages heat and tantalum empowers circuits.";echo;b "1. Lutetium (Lu)" "2. Hafnium (Hf)" "3. Tantalum (Ta)" "" "Input required: Select an element (1-3) or press 0 to exit.";};s(){ clear;b "Element Details" "Symbol: $1" "Name: $2" "Atomic Number: $3" "Atomic Weight: $4";echo;b "Description:" "$5";};e=("Lu|Lutetium|71|174.97|Lutetium is used in PET scanners and enhances imaging for diagnostics and research in nuclear medicine." "Hf|Hafnium|72|178.49|Hafnium is used in nuclear reactor control rods and aerospace alloys due to its neutron absorption." "Ta|Tantalum|73|180.95|Tantalum is corrosion-resistant and used in electronics, implants, and extreme-condition alloys.");while :;do d;echo -e "${E}";read -p "Enter your choice: " c;echo -e "${R}";[[ "$c" == 0 ]]&&clear&&b "Thank you!" "Exiting the 6 of Spades educational card."&&break;(( c>=1 && c<=3 ))&&IFS='|' read -r y n a w m<<<"${e[$((c-1))]}"&&s "$y" "$n" "$a" "$w" "$m"&&echo -e "${E}"&&read -n1 -s -r -p "Press any key to return to the main menu..."&&echo -e "${R}"||b "Invalid Input" "Please enter a valid choice (1-3) or 0 to exit.";done
