#!/bin/bash
E='\x1b[38;5;85m';R='\033[0m';w(){ echo "$1"|fold -s -w 78; };b(){ a=("$@");printf "${E}+";printf -- '-%.0s' $(seq 1 80);printf "+${R}\n";for l in "${a[@]}";do w "$l"|while IFS= read -r x;do printf "${E}|${R} %-78s ${E}|${R}\n" "$x";done;done;printf "${E}+";printf -- '-%.0s' $(seq 1 80);printf "+${R}\n";};d(){ clear;b "7 of Spades: W tungsten, Re rhenium and Os osmium" "These elements endure the extremes-tungsten withstands the hottest tasks, rhenium fortifies jet engines, and osmium adds dense durability to precision instruments.";echo;b "1. Tungsten (W)" "2. Rhenium (Re)" "3. Osmium (Os)" "" "Input required: Select an element (1-3) or press 0 to exit.";};s(){ clear;b "Element Details" "Symbol: $1" "Name: $2" "Atomic Number: $3" "Atomic Weight: $4";echo;b "Description:" "$5";};e=("W|Tungsten|74|183.84|Tungsten has the highest melting point of all metals and is used in cutting tools, filaments for lighting, and aerospace applications requiring extreme heat resistance." "Re|Rhenium|75|186.21|Rhenium is essential for superalloys used in jet engines and gas turbines. It enhances the heat resistance and mechanical strength of these materials." "Os|Osmium|76|190.23|Osmium is one of the densest elements and is used in electrical contacts, fountain pen tips, and scientific instruments where durability and precision are critical.");while :;do d;echo -e "${E}";read -p "Enter your choice: " c;echo -e "${R}";[[ "$c" == 0 ]]&&clear&&b "Thank you!" "Exiting the 7 of Spades educational card."&&break;(( c>=1 && c<=3 ))&&IFS='|' read -r y n a w m<<<"${e[$((c-1))]}"&&s "$y" "$n" "$a" "$w" "$m"&&echo -e "${E}"&&read -n1 -s -r -p "Press any key to return to the main menu..."&&echo -e "${R}"||b "Invalid Input" "Please enter a valid choice (1-3) or 0 to exit.";done
