#!/bin/bash
E='\x1b[38;5;85m';R='\033[0m';w(){ echo "$1"|fold -s -w 78; };b(){ a=("$@");printf "${E}+";printf -- '-%.0s' $(seq 1 80);printf "+${R}\n";for l in "${a[@]}";do w "$l"|while IFS= read -r x;do printf "${E}|${R} %-78s ${E}|${R}\n" "$x";done;done;printf "${E}+";printf -- '-%.0s' $(seq 1 80);printf "+${R}\n";};d(){ clear;b "8 of Spades: Ir iridium, Pt platinum and Au gold" "These precious metals offer unmatched durability and conductivity-iridium equips space tools, platinum fuels clean energy, and gold powers electronics.";echo;b "1. Iridium (Ir)" "2. Platinum (Pt)" "3. Gold (Au)" "" "Input required: Select an element (1-3) or press 0 to exit.";};s(){ clear;b "Element Details" "Symbol: $1" "Name: $2" "Atomic Number: $3" "Atomic Weight: $4";echo;b "Description:" "$5";};e=("Ir|Iridium|77|192.22|Iridium is one of the densest and most corrosion-resistant metals, making it ideal for spark plugs, space vehicle components, and scientific instruments." "Pt|Platinum|78|195.08|Platinum is widely used in catalytic converters, medical implants, and fuel cells. Its catalytic properties are essential in industrial and environmental applications." "Au|Gold|79|196.97|Gold is a highly conductive and corrosion-resistant metal, making it indispensable for electronics, spacecraft components, and precision medical equipment.");while :;do d;echo -e "${E}";read -p "Enter your choice: " c;echo -e "${R}";[[ "$c" == 0 ]]&&clear&&b "Thank you!" "Exiting the 8 of Spades educational card."&&break;(( c>=1 && c<=3 ))&&IFS='|' read -r y n a w m<<<"${e[$((c-1))]}"&&s "$y" "$n" "$a" "$w" "$m"&&echo -e "${E}"&&read -n1 -s -r -p "Press any key to return to the main menu..."&&echo -e "${R}"||b "Invalid Input" "Please enter a valid choice (1-3) or 0 to exit.";done
