#!/bin/bash
E='\x1b[38;5;85m';R='\033[0m';w(){ echo "$1"|fold -s -w 78; };b(){ a=("$@");printf "${E}+";printf -- '-%.0s' $(seq 1 80);printf "+${R}\n";for l in "${a[@]}";do w "$l"|while IFS= read -r x;do printf "${E}|${R} %-78s ${E}|${R}\n" "$x";done;done;printf "${E}+";printf -- '-%.0s' $(seq 1 80);printf "+${R}\n";};d(){ clear;b "10 of Spades: Bi Bismuth, Po Polonium and At Astatine" "These post-transition elements support medicine and industry-bismuth soothes the body, polonium powers nuclear tech, and astatine may treat cancer.";echo;b "1. Bismuth (Bi)" "2. Polonium (Po)" "3. Astatine (At)" "" "Input required: Select an element (1-3) or press 0 to exit.";};s(){ clear;b "Element Details" "Symbol: $1" "Name: $2" "Atomic Number: $3" "Atomic Weight: $4";echo;b "Description:" "$5";};e=("Bi|Bismuth|83|208.98|Bismuth is a non-toxic metal used in pharmaceuticals, such as stomach-relief medications. It is also applied in low-melting alloys for fire detection systems." "Po|Polonium|84|209|Polonium is a radioactive element used in nuclear batteries and as a heat source in space missions. Its intense radioactivity requires specialized handling." "At|Astatine|85|210|Astatine is the rarest naturally occurring element on Earth. It is studied for potential applications in targeted cancer therapies due to its radioactivity.");while :;do d;echo -e "${E}";read -p "Enter your choice: " c;echo -e "${R}";[[ "$c" == 0 ]]&&clear&&b "Thank you!" "Exiting the 10 of Spades educational card."&&break;(( c>=1 && c<=3 ))&&IFS='|' read -r y n a w m<<<"${e[$((c-1))]}"&&s "$y" "$n" "$a" "$w" "$m"&&echo -e "${E}"&&read -n1 -s -r -p "Press any key to return to the main menu..."&&echo -e "${R}"||b "Invalid Input" "Please enter a valid choice (1-3) or 0 to exit.";done
