#!/bin/bash
E='\x1b[38;5;85m';R='\033[0m';w(){ echo "$1"|fold -s -w 78; };b(){ a=("$@");printf "${E}+";printf -- '-%.0s' $(seq 1 80);printf "+${R}\n";for l in "${a[@]}";do w "$l"|while IFS= read -r x;do printf "${E}|${R} %-78s ${E}|${R}\n" "$x";done;done;printf "${E}+";printf -- '-%.0s' $(seq 1 80);printf "+${R}\n";};d(){ clear;b "King of Spades: U Uranium, Np Neptunium and Pu Plutonium" "These actinides are critical in nuclear energy and physics. Uranium fuels reactors, neptunium enables space batteries, and plutonium drives spacecraft and weapons.";echo;b "1. Uranium (U)" "2. Neptunium (Np)" "3. Plutonium (Pu)" "" "Input required: Select an element (1-3) or press 0 to exit.";};s(){ clear;b "Element Details" "Symbol: $1" "Name: $2" "Atomic Number: $3" "Atomic Weight: $4";echo;b "Description:" "$5";};e=("U|Uranium|92|238.03|Uranium is a dense, radioactive metal widely used as fuel in nuclear reactors. Its isotopes are also used in nuclear weapons and research on nuclear energy." "Np|Neptunium|93|237|Neptunium is a byproduct of nuclear reactors and has applications in radioisotope batteries for space exploration. Its radioactive properties are studied in nuclear science." "Pu|Plutonium|94|244|Plutonium is used in nuclear reactors, nuclear weapons, and spacecraft power systems. Its isotopes are a key energy source for long-duration space missions.");while :;do d;echo -e "${E}";read -p "Enter your choice: " c;echo -e "${R}";[[ "$c" == 0 ]]&&clear&&b "Thank you!" "Exiting the King of Spades educational card."&&break;(( c>=1 && c<=3 ))&&IFS='|' read -r y n a w m<<<"${e[$((c-1))]}"&&s "$y" "$n" "$a" "$w" "$m"&&echo -e "${E}"&&read -n1 -s -r -p "Press any key to return to the main menu..."&&echo -e "${R}"||b "Invalid Input" "Please enter a valid choice (1-3) or 0 to exit.";done
