#!/bin/bash
E='\x1b[38;5;85m';R='\033[0m';w(){ echo "$1"|fold -s -w 78; };b(){ a=("$@");printf "${E}+";printf -- '-%.0s' $(seq 1 80);printf "+${R}\n";for l in "${a[@]}";do w "$l"|while IFS= read -r x;do printf "${E}|${R} %-78s ${E}|${R}\n" "$x";done;done;printf "${E}+";printf -- '-%.0s' $(seq 1 80);printf "+${R}\n";};d(){ clear;b "4 of Clubs: Ra Radium, Ac Actinium and Th Thorium" "These radioactive elements are used for energy, research and medical systems. Radium glows, actinium treats cancer and thorium powers future reactors.";echo;b "1. Radium (Ra)" "2. Actinium (Ac)" "3. Thorium (Th)" "" "Input required: Select an element (1-3) or press 0 to exit.";};s(){ clear;b "Element Details" "Symbol: $1" "Name: $2" "Atomic Number: $3" "Atomic Weight: $4";echo;b "Description:" "$5";};e=("Ra|Radium|88|226|Radium was once used in glowing watch dials and scientific devices. Its radioactivity makes it historic and useful for studying radiation in space systems." "Ac|Actinium|89|227|Actinium is valuable in radiation therapy for cancer treatment. Its high-energy emissions offer medical solutions for astronauts in deep space." "Th|Thorium|90|232.04|Thorium is a promising nuclear fuel. It is safer and more abundant than uranium and could support long-term space missions with cleaner energy.");while :;do d;echo -e "${E}";read -p "Enter your choice: " c;echo -e "${R}";[[ "$c" == 0 ]]&&clear&&b "Thank you!" "Exiting the 4 of Clubs educational card."&&break;(( c>=1 && c<=3 ))&&IFS='|' read -r y n a w m<<<"${e[$((c-1))]}"&&s "$y" "$n" "$a" "$w" "$m"&&echo -e "${E}"&&read -n1 -s -r -p "Press any key to return to the main menu..."&&echo -e "${R}"||b "Invalid Input" "Please enter a valid choice (1-3) or 0 to exit.";done
