#!/bin/bash
E='\x1b[38;5;85m';R='\033[0m';w(){ echo "$1"|fold -s -w 78; };b(){ a=("$@");printf "${E}+";printf -- '-%.0s' $(seq 1 80);printf "+${R}\n";for l in "${a[@]}";do w "$l"|while IFS= read -r x;do printf "${E}|${R} %-78s ${E}|${R}\n" "$x";done;done;printf "${E}+";printf -- '-%.0s' $(seq 1 80);printf "+${R}\n";};d(){ clear;b "10 of Clubs: Sg Seaborgium, Bh Bohrium and Hs Hassium" "These superheavy elements are lab-synthesized to study nuclear structure and extreme atomic behavior-seaborgium explores transuranics, bohrium refines atomic models, and hassium probes nuclear decay.";echo;b "1. Seaborgium (Sg)" "2. Bohrium (Bh)" "3. Hassium (Hs)" "" "Input required: Select an element (1-3) or press 0 to exit.";};s(){ clear;b "Element Details" "Symbol: $1" "Name: $2" "Atomic Number: $3" "Atomic Weight: $4";echo;b "Description:" "$5";};e=("Sg|Seaborgium|106|271|Seaborgium is a synthetic element used to study heavy nuclei and named after Glenn T. Seaborg, a key contributor to actinide research." "Bh|Bohrium|107|274|Bohrium is named after Niels Bohr and is synthesized for examining nuclear and chemical behavior at high atomic numbers." "Hs|Hassium|108|277|Hassium is researched for potential nuclear stability and offers insight into superheavy decay processes.");while :;do d;echo -e "${E}";read -p "Enter your choice: " c;echo -e "${R}";[[ "$c" == 0 ]]&&clear&&b "Thank you!" "Exiting the 10 of Clubs educational card."&&break;(( c>=1 && c<=3 ))&&IFS='|' read -r y n a w m<<<"${e[$((c-1))]}"&&s "$y" "$n" "$a" "$w" "$m"&&echo -e "${E}"&&read -n1 -s -r -p "Press any key to return to the main menu..."&&echo -e "${R}"||b "Invalid Input" "Please enter a valid choice (1-3) or 0 to exit.";done
