#!/bin/bash
E='\x1b[38;5;85m';R='\033[0m';w(){ echo "$1"|fold -s -w 78; };b(){ a=("$@");printf "${E}+";printf -- '-%.0s' $(seq 1 80);printf "+${R}\n";for l in "${a[@]}";do w "$l"|while IFS= read -r x;do printf "${E}|${R} %-78s ${E}|${R}\n" "$x";done;done;printf "${E}+";printf -- '-%.0s' $(seq 1 80);printf "+${R}\n";};d(){ clear;b "Queen of Clubs: Cn Copernicium, Nh Nihonium, Fl Flerovium" "These synthetic elements push scientific boundaries with advanced synthesis and analysis-copernicium honors Copernicus, nihonium hails from Japan, and flerovium comes from the Flerov Laboratory.";echo;b "1. Copernicium (Cn)" "2. Nihonium (Nh)" "3. Flerovium (Fl)" "" "Input required: Select an element (1-3) or press 0 to exit.";};s(){ clear;b "Element Details" "Symbol: $1" "Name: $2" "Atomic Number: $3" "Atomic Weight: $4";echo;b "Description:" "$5";};e=("Cn|Copernicium|112|285|Copernicium is named after Nicolaus Copernicus and studied for chemical behavior and superheavy stability." "Nh|Nihonium|113|286|Nihonium was synthesized in Japan and is part of research into superheavy element behavior." "Fl|Flerovium|114|289|Flerovium is named after the Flerov Laboratory and helps refine nuclear decay and stability models.");while :;do d;echo -e "${E}";read -p "Enter your choice: " c;echo -e "${R}";[[ "$c" == 0 ]]&&clear&&b "Thank you!" "Exiting the Queen of Clubs educational card."&&break;(( c>=1 && c<=3 ))&&IFS='|' read -r y n a w m<<<"${e[$((c-1))]}"&&s "$y" "$n" "$a" "$w" "$m"&&echo -e "${E}"&&read -n1 -s -r -p "Press any key to return to the main menu..."&&echo -e "${R}"||b "Invalid Input" "Please enter a valid choice (1-3) or 0 to exit.";done
