#!/bin/bash
E='\x1b[38;5;46m';R='\033[0m';w(){ [ -n "$1" ]&&echo "$1"|fold -s -w 60||echo " "; };b(){ a=("$@");printf "${E}+";printf -- '-%.0s' $(seq 1 60);printf "+${R}\n";for l in "${a[@]}";do w "$l"|while IFS= read -r x;do printf "${E}|${R} %-58s ${E}|${R}\n" "$x";done;done;printf "${E}+";printf -- '-%.0s' $(seq 1 60);printf "+${R}\n";};pause(){ echo;read -n1 -s -r -p "SCAN TO CONTINUE..."; };choose(){ clear;b "SELECT A TEXT FILE";ls *.txt 2>/dev/null|nl;read -p "FILE #: " f;files=($(ls *.txt 2>/dev/null));target="${files[$((f-1))]}";};show_cat(){ clear;b "CAT VIEW: $target";cat "$target" 2>/dev/null||echo "Not found";pause;};show_head(){ clear;b "HEAD VIEW (Top 10): $target";head "$target" 2>/dev/null||echo "Not found";pause;};show_less(){ clear;b "LESS VIEW: $target";less "$target";pause;};edit_nano(){ clear;b "EDITING IN NANO: $target";nano "$target";pause;};while true;do clear;b "BAAASH SYSTEMS INTERFACE 41386 - 9 OF SPADES" "MISSION: FILE VIEWING & EDITING" "TOOLS: cat, head, less, nano" "MODE: SCANNER ONLY";b "A = Choose File" "B = cat (print entire file)" "C = head (top of file)" "D = less (scrolling view)" "E = nano (editor)" "Q = Exit Module";read -p "SCAN CODE: " x;case "${x^^}" in A)choose;;B)show_cat;;C)show_head;;D)show_less;;E)edit_nano;;Q)clear;b "MODULE COMPLETE";break;;*)b "INVALID SCAN";pause;;esac;done
