#!/bin/bash
E='\x1b[38;5;46m';R='\033[0m';w(){ fmt -w 60 <<<"$1";};b(){ a=("$@");printf "${E}+";printf -- '-%.0s' $(seq 1 60);printf "+${R}\n";for l in "${a[@]}";do w "$l"|while IFS= read -r x;do printf "${E}|${R} %-58s ${E}|${R}\n" "$x";done;done;printf "${E}+";printf -- '-%.0s' $(seq 1 60);printf "+${R}\n";};pause(){ echo;read -n1 -s -r -p "SCAN TO CONTINUE...";};m_cp(){ clear;b "COPY MODE ACTIVE" "Duplicate a file using scanner prompt.";read -p "SCAN FILENAME TO COPY: " src;read -p "SCAN DESTINATION NAME: " dst;cp "$src" "$dst"&&echo "COPIED: $src âœ $dst"||echo "COPY FAILED";pause;};m_mv(){ clear;b "MOVE MODE ACTIVE" "Rename or relocate a file.";read -p "SCAN SOURCE FILE: " src;read -p "SCAN NEW DESTINATION: " dst;mv "$src" "$dst"&&echo "MOVED: $src âœ $dst"||echo "MOVE FAILED";pause;};m_rm(){ clear;b "DELETE MODE ACTIVE" "Remove file permanently.";read -p "SCAN FILE TO DELETE: " f;rm -i "$f";pause;};m_touch(){ clear;b "CREATE MODE ACTIVE" "Create a blank file.";read -p "SCAN NEW FILENAME: " f;touch "$f"&&echo "CREATED: $f"||echo "FAILED TO CREATE";pause;};while :;do clear;b "BAAASH SYSTEMS INTERFACE 41386 - 3 OF SPADES" "MISSION: FILE MANAGEMENT MODULE" "TOOLS: CP, MV, RM, TOUCH" "MODE: SCANNER ONLY - NO KEYBOARD ENTRY" "" "FILESYSTEM OPERATIONS BEGIN HERE.";b "SCAN MENU" "A = COPY FILE" "B = MOVE OR RENAME" "C = DELETE FILE" "D = CREATE NEW FILE" "Q = EXIT FILE MGMT MODULE";read -p "SCAN CODE: " c;case "${c^^}" in A)m_cp;;B)m_mv;;C)m_rm;;D)m_touch;;Q)clear;b "MODULE COMPLETE - 3 OF SPADES EXITED";break;;*)b "INVALID SCAN" "VALID: A B C D Q";pause;;esac;done
