#!/bin/bash
E='\x1b[38;5;46m';R='\033[0m';w(){ [ -n "$1" ]&&echo "$1"|fold -s -w 60||echo " "; };b(){ a=("$@");printf "${E}+";printf -- '-%.0s' $(seq 1 60);printf "+${R}\n";for l in "${a[@]}";do w "$l"|while IFS= read -r x;do printf "${E}|${R} %-58s ${E}|${R}\n" "$x";done;done;printf "${E}+";printf -- '-%.0s' $(seq 1 60);printf "+${R}\n";};pause(){ echo;read -n1 -s -r -p "SCAN TO CONTINUE...";};sg(){ while true;do clear;b "7♠ GREP SCANNER - TEXT IN FILES" "GREP searches inside files for matching words or patterns." "Used in troubleshooting, security checks, and config parsing." "A = Look for 'error' in /var/log" "B = Look for 'ssh' in /etc" "C = Look for current user in /home" "X = Return";read -p "SCAN: " x;case "${x^^}" in A)clear;b "Scanning /var/log for 'error'...";grep -iR 'error' /var/log 2>/dev/null|head -n 20;;B)clear;b "Scanning /etc for 'ssh'...";grep -iR 'ssh' /etc 2>/dev/null|head -n 20;;C)clear;b "Scanning /home for $USER...";grep -iR "$USER" /home 2>/dev/null|head -n 20;;X)break;;*)b "INVALID INPUT";esac;pause;done;};sf(){ while true;do clear;b "7♠ FIND TOOL - FILE HUNTER" "FIND traverses the filesystem in real time to locate files." "Best for recursive deep scans or file date-based filters." "A = *.conf in /etc" "B = *.log in /var/log" "C = *.sh in /home" "D = Modified <1 day in /etc" "X = Return";read -p "SCAN: " x;case "${x^^}" in A)clear;b "Looking for *.conf in /etc...";find /etc -type f -name "*.conf" 2>/dev/null|head -n 20;;B)clear;b "Looking for *.log in /var/log...";find /var/log -type f -name "*.log" 2>/dev/null|head -n 20;;C)clear;b "Looking for *.sh in /home...";find /home -type f -name "*.sh" 2>/dev/null|head -n 20;;D)clear;b "Modified under 1 day in /etc...";find /etc -type f -mtime -1 2>/dev/null|head -n 20;;X)break;;*)b "INVALID INPUT";esac;pause;done;};sl(){ while true;do clear;b "7♠ LOCATE DATABASE - INDEXED SEARCH" "LOCATE is faster than find because it searches a cached index." "Best for instantly finding known filenames." "A = passwd locations" "B = bashrc files" "C = ssh_config files" "D = *.service units" "X = Return";read -p "SCAN: " x;case "${x^^}" in A)clear;b "LOCATE: passwd files";locate passwd|grep '/passwd$'|head -n 20;;B)clear;b "LOCATE: bashrc files";locate bashrc|head -n 20;;C)clear;b "LOCATE: ssh_config";locate ssh_config|head -n 20;;D)clear;b "LOCATE: *.service units";locate .service|head -n 20;;X)break;;*)b "INVALID INPUT";esac;pause;done;};while true;do clear;b "BAAASH SYSTEMS INTERFACE 41386 - 7 OF SPADES" "MISSION: FILE SEARCH" "MODE: SCANNER ONLY - NO KEYBOARD ENTRY" "TOOLS: grep (search text), find (recursive scan), locate (indexed lookup)";b "SCAN MENU" "A = GREP Scanner (text inside files)" "B = FIND Tool (deep path scan)" "C = LOCATE Index (fast cached search)" "Q = Exit Training Module";read -p "SCAN CODE: " x;case "${x^^}" in A)sg;;B)sf;;C)sl;;Q)clear;b "MODULE COMPLETE - 7 OF SPADES FINISHED";break;;*)b "INVALID SCAN CODE";pause;;esac;done
